/*Table structure for table `announcement` */

DROP TABLE IF EXISTS `announcement`;

CREATE TABLE `announcement` (
  `id` int unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `user_id` bigint unsigned NOT NULL DEFAULT '0' COMMENT '发布者ID',
  `content` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '内容',
  `status` char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '1' COMMENT '状态：(0 启用；1 待用；2 停用；)',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=21 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci COMMENT='公告表';

/*Data for the table `announcement` */

insert  into `announcement`(`id`,`user_id`,`content`,`status`,`create_time`,`update_time`) values (17,1,'2022年5月12日，经历网上线测试！','2','2022-05-12 03:57:33','2022-05-12 06:01:19'),(18,1,'经历网，人人可免费投放个人经历！投放个人经历，请参考下面的文章推荐《如何书写和免费投放个人经历》！','2','2022-05-20 10:18:14','2022-05-31 11:00:25'),(20,1,'投放个人经历邮箱：908866805@qq.com','0','2022-07-11 05:18:16','2022-07-11 13:18:15');

/*Table structure for table `aphorism` */

DROP TABLE IF EXISTS `aphorism`;

CREATE TABLE `aphorism` (
  `id` int unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `content` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '内容',
  `author` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '作者',
  `is_deleted` bit(1) NOT NULL DEFAULT b'0' COMMENT '逻辑删除：(0 正常；1 已删除)',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=122 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci COMMENT='随机诗语';

/*Data for the table `aphorism` */

insert  into `aphorism`(`id`,`content`,`author`,`is_deleted`,`create_time`) values (1,'我们为什么要努力？每个人的答案都不同。时间对待所有人都是公平的，是努力让每段人生不同。努力，能成全更多小小的梦想，能让你遇见更好的自己，能让你在回首时说一句问心无愧。','','\0','2019-06-16 09:09:14'),(2,'这个世界上有很多人比你更优秀，但我们不能因此就放弃变得更好。不断向前奔跑是一种人生姿态，是对自我的肯定和尊重，也是对生活的理解与热爱。也许你跑不过别人，但一定要跑过昨天的自己。','','\0','2020-06-16 09:10:08'),(3,'世界上两种耀眼的光芒，一种是阳光，一种是你努力时散发的光。','','\0','2021-06-16 09:11:21'),(4,'只要学不死，就往死里学。','狂神说','\0','2021-06-16 09:11:25'),(5,'喷泉之所以漂亮是因为她有了压力；瀑布之所以壮观是因为她没有了退路；水之所以能穿石是因为永远在坚持。人生亦是如此。\r\n走好选择的路，别选择好走的路。','杨绛','\0','2021-06-16 09:12:55'),(6,'选择和什么样的人交往是一件非常重要的事情，他们会潜移默化的影响着你对生活的态度，以及看世界的角度。','','\0','2021-06-16 09:12:57'),(7,'如果要飞，我必须担起，掉落的伤痛。慢慢的破壳尔出，直到成为会飞的天使。','','\0','2021-06-16 09:13:29'),(8,'我们可以欺瞒别人，却无法欺瞒自己，当我们走向枝繁叶茂的五月，青春就不再是一个谜，向上的路，总是坎坷又崎岖，要永远保持最初的浪漫，真是不容易，有人悲哀，有人欣喜，当我们跨越了一座高山，也就跨越了一个真实的自己。','汪国真','\0','2021-06-16 09:13:31'),(9,'别用悲伤的语调对我诉说，人生只是一个虚幻的梦乡；因为沉睡中的灵魂已死去，万物并不像看上去那模样。我们命定的前路，既不是享乐，也不是忧伤；而是要行动，让每一个明天，都比今天奔向更远的地方。','','\0','2021-06-16 09:13:34'),(10,'如果你偶然想起了我，我便为你唱歌。雨后的黄昏把她的阴影洒在河面上，把她的暗淡的光缓缓托向西方；余晖脉脉，已不适于劳作或游戏。你坐在向南的露台上，我在黑暗的房间里为你唱歌。暮色苍茫，从窗栊飘进湿润的绿叶的清香，预告雷雨将至的狂风在椰林里咆哮。掌灯时分，我将离去。当你倾听着夜间的天籁，那是也许你能听到我的歌声，虽然我已不再唱歌。','泰戈尔','\0','2021-06-16 09:13:37'),(11,'我爱你，不光因为你的样子，还因为，和你在一起时，我的样子。我爱你，不光因为你为我而做的事，还因为，为了你，我能做成的事。我爱你，因为你能唤出，我最真的那部分。我爱你，因为你穿越我心灵的旷野，如同阳光穿越水晶般容易。我的傻气，我的弱点，在你的目光里几乎不存在。而我心里最美丽的地方，却被你的光芒照得通亮。','罗伊·克里夫特','\0','2021-06-16 09:13:39'),(12,'有一天，当我死了，想到你会流泪，我已如此幸福。真想告诉你你是我一生中的一件最美好的事，当你死了，当你回到落叶化成的泥土，我将认出你，我的心将挨着你，不声不响，你知道是我，我知道是你。 ','王海桑','\0','2021-06-16 09:13:42'),(13,'我曾经爱过你，爱情，也许在我的心灵里，还没有完全消亡，但愿它不会再去打扰你；我也不想再让你难过悲伤，我曾经默默无语地，毫无指望地爱过你，我既忍受着羞怯，又忍受着嫉妒的折磨，我曾经那样真诚，那样温柔地爱过你，但愿上帝保佑你，另一个人也会象我一样，爱你！','普希金','\0','2021-06-16 09:13:45'),(14,'不如就利用孤单一人的时间使自己变得更优秀，给来的人一个惊喜，也给自己一个好的交代。','','\0','2021-06-16 09:13:47'),(15,'也许现在的你很累，但未来的路还很长，不要忘了当初为何而出发，是什么让你坚持到现在，勿忘初心。丢失的自己 ，只能一点一点捡回来。把委屈和泪水都咽下去，输不起就不要输，死不了就站起来，告诉所有看不起你的人：我很好。','','\0','2021-06-16 09:15:51'),(16,'我的心灵和我的一切，我都愿你拿去，只求你给我留一双眼睛，让我能看到你。在我的身上，没有不曾被你征服的东西，你夺去了它的生命， 也就将它的死亡携去。如果我还需失掉什么，但愿你将我带去，只求你给我留一双眼睛，让我能看到你。','卡蒙斯【葡萄牙】 翻译：肖佳平','\0','2021-06-16 09:15:53'),(17,'一切都是命运，一切都是烟云，一切都是没有结局的开始，一切都是稍纵即逝的追寻，一切欢乐都没有微笑，一切苦难都没有泪痕，一切语言都是重复，一切交往都是初逢，一切爱情都在心里，一切往事都在梦中，一切希望都带着注释，一切信仰都带着呻吟，一切爆发都有片刻的宁静，一切死亡都有冗长的回声。 ','北岛','\0','2021-06-16 09:15:55'),(18,'如果生命是爱，多么好！我想要更多生命去爱！今天我感到，一千年的思想，值不上情绪蓝调的一分钟。我的心曾经悲伤迟缓地死去……今天它在光亮里打开像太阳神的花，生命绽放如暴烈的海，爱的手在里面捶击！今天我的忧郁向着黑夜出发，悲伤，寒冷，翅膀断裂；在远方的阴影里松开自己，像一块痛苦的旧斑痕…… 我全部的生命唱歌，亲吻，大笑！我全部的生命，一张鲜花怒放的嘴！','德尔米拉·阿古斯蒂妮 翻译：汪天艾','\0','2021-06-16 09:15:58'),(19,'不抱怨过去的挫折，不畏惧未来的挑战。把每一天都当做全新的开始，不为别人的掌声，只为成为更好的自己。','','\0','2021-06-16 09:16:01'),(20,'一个人最好的状态莫过于悄悄地努力，不羡慕谁，不嘲笑谁，也不依赖谁，扛过困难，提升格局，活成自己喜欢的样子。向上的路总是难走的，但却值得拼尽全力，因为一旦踏上去，每一步都不会白费。','','\0','2021-06-16 09:17:08'),(21,'一个人首先具有人的起码尊严，不容侵犯。如果受到侵犯，他将誓死抗争。他会有尊严地活着，有尊严地做事，做有尊严的事，也就是说他不做有损自己尊严的事，也不允许别人使其失去尊严。一个人尊重他人的人格，也要求自己的人格受到他人的尊重。','阿尔贝 • 加缪','\0','2021-06-16 14:00:43'),(22,'真正的救赎，并不是厮杀后的胜利，而是能在苦难之中，找到生的力量和心的安宁。','阿尔贝 • 加缪','\0','2021-06-16 15:06:46'),(23,'不要走在我后面，因为我可能不会引路。不要走在我前面，因为我可能不会跟随。请走在我的身边，做我的朋友。','阿尔贝 • 加缪','\0','2021-06-16 15:31:25'),(24,'我不去想，是否能够成功，既然选择了远方，便只顾风雨兼程。我不去想，能否赢得爱情，既然钟情于玫瑰，就勇敢地吐露真诚 。我不去想，身后会不会袭来寒风冷雨，既然目标是地平线，留给世界的只能是背影。我不去想，未来是平坦还是泥泞，只要热爱生命 ，一切，都在意料之中。','汪国真','\0','2021-09-05 22:44:00'),(25,'我躺在山的怀里，看星星跳舞，整个夜空都在抽搐，树枝尖叫，风疯了，尘暴是大地掩饰悲愤的面纱，闪电雷雨齐奏命运交响曲，躺在月亮之上的一颗心，泪了。我皱眉，起身理一理衣裙，不留下一丝痛楚。','Bing','','2021-09-05 22:49:26'),(26,'她终将葬身于欲望的海洋，带着她形成欲望的苦难，和永不甘心的倔强，向世界挥拳，讨债无果般，阴魂不散；那所谓伟大的梦想，坚贞的理想，不过是一个贱民的呐喊，为了可笑的尊严，歇斯底里地狂妄，视失败如药膳；她必须痊愈，这是上帝的游戏，上帝许诺，给她女儿躯，完前世男儿志。','Bing','','2021-09-05 22:53:31'),(27,'有一种东西，渐渐注入我的心里，像恶魔的咒语，操纵着我去霸占别人的奇迹；在许多个无眠煎熬的夜里，我挣扎得大汗淋漓，始终架不住道德的底线在梦里一次又一次崩塌彻底；恶魔的召唤从未停歇，日渐剧烈，使我想毁灭一切，杀死自己，保全我于人间最后的一丝善意。','Bing','','2021-09-05 22:57:01'),(28,'因为我爱你，所以你才如此美丽，别人也用赞叹的目光，看着你。因为我爱你，所以你才青春不去，眼角没有皱纹，额头没有白发一缕。因为我爱你，所以你才如此美丽！但是切莫趾高气扬，也不要施展诡计!如果我的爱情消逝，如果我的心不再看得起你，转瞬你就变得衰老不堪，变得丑陋无比！','大卫.奥瓦迪亚 【杨燕杰译】','\0','2021-09-05 23:11:28'),(29,'醉过才知酒浓，爱过才知情重。你不能做我的诗，我不能做你的梦。','','\0','2021-09-05 23:15:02'),(30,'假如我来世上一遭，只为与你相聚一次，只为了亿万光年里的那一刹那，一刹那里所有的甜蜜与悲凄，那麽就让一切该发生的都在瞬间出现吧。我俯首感谢所有星球的相助，让我与你相遇，与你别离，完成了上帝所作的一首诗，然後再缓缓地老去。','席慕容','\0','2021-09-05 23:25:56'),(31,'其实我盼望的，也但是就只是那一瞬，我从没要求过你给我，你的一生，如果能在开满了栀子花的山坡上，与你相遇如果能，深深地爱过一次再别离，那麽再长久的一生，不也就只是就只是，回首时，那短短的一瞬。','席慕容','\0','2021-09-05 23:30:38'),(32,'我是天空里的一片云，偶尔投影在你的波心，你不必讶异，更无须欢喜,在转瞬间消灭了踪影。你我相逢在黑夜的海上，你有你的，我有我的，方向；你记得也好，最好你忘掉,在这交会时互放的光亮！','徐志摩','\0','2021-09-05 23:34:23'),(33,'你站在桥上看风景，看风景的人在楼上看你。明月装饰了你的窗子，你装饰了别人的梦。','卞之琳','\0','2021-09-05 23:39:12'),(34,'成功的花，人们只惊慕她现时的明艳，然而当初她的芽儿，浸透了奋斗的泪泉，洒遍了牺牲的血雨。','冰心','\0','2021-09-05 23:42:48'),(35,'你见，或者不见我，我就在那里，不悲不喜，你念，或者不念我，情就在那里，不来不去，你爱或者不爱我，爱就在那里，不增不减，你跟，或者不跟我，我的手就在你的手里，不舍不弃。来我怀里，或者，让我住进你的心里，默然相爱，寂静欢喜。','仓央嘉措','\0','2021-09-05 23:45:56'),(36,'许多夜晚重叠，悄然形成黑暗，玫瑰吸收光芒，大地按捺清香，为了寻找你，我搬进鸟的眼睛，经常盯着路过的风。',' 毕赣','\0','2021-09-05 23:57:35'),(37,'所有的结局都已写好，所有的泪水也都已启程，却忽然忘了是怎麽样的一个开始，在那个古老的不再回来的夏日，无论我如何地去追索，年轻的你只如云影掠过，而你微笑的面容极浅极淡，逐渐隐没在日落后的群岚，遂翻开那发黄的扉页，命运将它装订得极为拙劣，含着泪我一读再读，却不得不承认，青春是一本太仓促的书。','席慕容','\0','2021-09-06 00:04:20'),(38,'从明天起，做一个幸福的人，喂马，劈柴，周游世界，从明天起，关心粮食和蔬菜，我有一所房子，面朝大海，春暖花开，从明天起，和每一个亲人通信，告诉他们我的幸福，那幸福的闪电告诉我的，我将告诉每一个人，给每一条河每一座山，取一个温暖的名字。陌生人，我也为你祝福，愿你有一个灿烂的前程，愿你有情人终成眷属，愿你在尘世获得幸福，我只愿面朝大海，春暖花开。','海子','\0','2021-09-06 00:07:21'),(39,'我听过世间最美的情话，爱过人间最美的男子。我们曾恩爱浪漫至极，最后痛心绝望至死。','','\0','2021-09-06 00:08:58'),(40,'我想把世界上最好的都给你，却发现世界上最好的都是你。','','\0','2021-09-13 14:50:12'),(41,'成熟的最大好处是：以前得不到的东西，现在不想要了。 ','','\0','2021-09-13 15:04:41'),(42,'真正的平静，不是避开车马喧嚣，而是在心中修篱种菊。 ','','\0','2021-09-13 15:05:11'),(43,'没有人能替你承受痛苦，也没有人能拿走你的坚强。 ','','\0','2021-09-13 15:17:10'),(44,'一个不努力的人，别人想拉你一把，都找不到你的手在哪里。 ','','\0','2021-09-13 15:17:35'),(45,'当你想放弃时，想想当初为什么坚持到现在。 ','','\0','2021-09-13 15:19:10'),(46,'用微笑告诉别人，今天的我，比昨天更强，今后也一样。 ','','\0','2021-09-13 15:19:31'),(47,'喜欢是声色犬马，一晌贪欢，而爱是过进千帆，温酒煮茶共杯盏，可锦衣绸缎，可粗茶淡饭，可遍尝人间，可归老山川。 ','','\0','2021-09-13 15:21:27'),(48,'纯则粹，阳则刚。天行健，两仪遵道恒长，故有长久者不自生方长生之讲。百丈峰，松如浪。地势坤，厚德载物之象，故君子不争炎凉。 ','','\0','2021-09-13 15:22:10'),(49,'时间很短，天涯很远，往后的一山一水，一朝一夕，自己安静地走完。倘若不慎走失迷途，跌入水中，也应该记得，有一条河流，叫重生。 ','','\0','2021-09-13 15:22:42'),(50,'那曾经有过的繁荣与梦想，一点一滴攒积于心。天上的星星，地上的眼睛。雾散，梦醒，我终于看见真实，那是千帆过尽的沉寂。','','\0','2021-09-13 15:23:48'),(51,'你我为了理想而历尽了艰难，才走向了成功之路。但愿你还能记得，永远地记得，那一段充 满着奋斗激情的闪亮日子。','','\0','2021-09-13 15:24:36'),(52,'不要过分的去批评别人，了解了一切，就会宽恕一切。 ','','\0','2021-09-13 15:24:59'),(53,'能用汗水解决的问题，就尽量别用泪水。努力到无能为力，拼搏到感动自己。 ','','\0','2021-09-13 15:26:05'),(54,'有些事不是看到了希望才去坚持，而是因为坚持了才会看到希望。 ','','\0','2021-09-13 15:26:19'),(55,'人生的价值，应当看他贡献什么，而不应当看他取得什么。 ','','\0','2021-09-13 15:26:38'),(56,'你只有走完必须走的路，才能过想过的生活。 ','','\0','2021-09-13 15:26:55'),(57,'世界很大，就像这星空一样，它不会因为，一颗星星的存在，而流光溢彩，也不会因为，一颗星星的缺席，就黯然失色。 ','','\0','2021-09-13 15:27:16'),(58,'你的人生永远不会辜负你，那些转错的弯、那些走错的路、那些流下的泪、那些滴下的汗水、那些留下的伤痕，全都让你成为独一无二的自己。 ','','\0','2021-09-13 15:27:55'),(59,'没有人会关心你付出过多少努力，撑得累不累，摔得痛不痛，他们只会看你最后站在什么位置，然后羡慕或鄙夷。 ','','\0','2021-09-13 15:28:40'),(60,'用百折不回的毅力，有计划地克服所有的困难。 ','','\0','2021-09-13 15:29:46'),(61,'愿你成为自己喜欢的样子，不抱怨，不将就，有野心，有光芒！ ','','\0','2021-09-13 15:31:12'),(62,'与其违心赔笑，不如一人安静，与其在意别人的背弃和不善，不如经营自己的尊严和美好。 ','','\0','2021-09-13 15:34:49'),(63,'如果你不想对一座城市失望，一定要在夜色中抵达。夜晚的灯光会巧妙地把璀璨突出，把粗陋隐藏。 ','','\0','2021-09-13 15:36:07'),(64,'我以为爱情可以填满人生的遗憾。然而，制造更多遗憾的。却偏偏是爱情。 ','','\0','2021-09-13 15:36:48'),(65,'彻底地失去一件东西，有时候比得到还要快乐，因为不必再苦苦守候了。 ','','\0','2021-09-13 15:37:34'),(66,'人们说，直到失去了你才会知道拥有过什么。事实上，你一直知道你拥有什么，只是你以为你永远不会失去它。 ','','\0','2021-09-13 15:38:16'),(67,'意志坚强的人，他的世界充满着无限的可能性。 ','','\0','2021-09-13 15:38:41'),(68,'无论你经历的事是喜是悲，无论你遇见的人是好是坏，他们总能教会你一些事理，然后助你成为一个更好的人。 ','','\0','2021-09-13 15:39:14'),(69,'其实,每一次的失败，都是成功的开始;每一次的泪水，都有一次的醒悟;每一次的磨难，都有生命的财富。每一次的伤痛，都是成长的支柱。每一次的打击，都是坚强的后盾;活着必定要经历一些挫折，这个世界，需要你用尽全力地生活!敢于面对，就必能胜出!','','\0','2021-09-13 15:40:53'),(70,'我在你身上犯过两个错，一开始是信以为自己会是无可替代的，再后来又误以为能有人可以替代你。','','\0','2021-09-13 15:41:27'),(71,'请找一个值得你爱的人去爱，让那些爱慕虚荣的，见异思迁的，虚有其表的，够不上了解你思想感情和人格的人，去找他志同道合的伴侣去吧。你总有一天会暗自庆幸，你幸亏没有得到他。','','\0','2021-09-13 15:42:08'),(72,'每个人都会有一段异常艰难的时光，生活的窘迫，工作的失意，学业的压力，爱的惶惶不可终日。挺过来的，人生就会豁然开朗;挺不过来的，时间也会教会你怎么与它们握手言和，所以你都不必害怕的。','','\0','2021-09-13 15:43:26'),(73,'真正的安全感，来自你对自己的信心，是你每个阶段性目标的实现，而真正的归属感，在于你的内心深处，对自己命运的把控，因为你最大的对手永远都是自己。一个人能够，并且应该让自己做到的，不是感到安全，而是能够接纳不安全的现实。','','\0','2021-09-13 15:44:51'),(74,'愿你所有快乐无需假装。愿你此生尽兴，赤诚善良。愿时光能缓，愿故人不散。愿有人陪你颠沛流离，愿你惦念的人能和你道晚安，愿你独闯的日子里不觉得孤单。','','\0','2021-09-13 15:45:23'),(75,'努力，不只是为了要赚多少钱，更多的时候，是为了心中的梦，一种尊严，一种责任，一种使命!是为了让自己的人生，变得更强大。','','\0','2021-09-13 15:46:25'),(76,'伟人改变环境，能人利用环境，凡人适应环境，庸人不适应环境。','','\0','2021-09-13 15:46:47'),(77,'想成为大树，就不要和草去比。短期来看，草的生长速度和树相比，肯定是草的长势明显，但是几年过后，草换了几拨，但是树依旧是树。所以这个世界上只有古树大树，却没有古草大草。做人做事，重要的不是一时的快慢，而是持久的发展力。','','\0','2021-09-13 15:47:21'),(78,'别人在熬夜时，你在睡觉，别人已经起床，你还在挣扎再多睡几分钟，你有很多想法，但脑袋热了就过了，别人却一件事坚持到底。你连一本书都要看很久，该工作的时候就刷起手机，肯定也不能早晨起来背单词，晚上加班到深夜。很多时候不是你平凡，碌碌无为，而是你没有别人付出得多。','','\0','2021-09-13 15:48:39'),(79,'一个人，最大的快乐，是有人问候。一个人，最大的幸福，是有人在乎;一个人，最大的感动，是有人包容;一个人，最大的满足，是有人守候;一个人，最大的奢求，是有人懂得;生活不要太享受，幸福简单就足够;人生不要太完美，快乐无悔就满足!','','\0','2021-09-13 15:49:14'),(80,'真正的坚强，是属于那些夜晚在被窝里哭泣，而白天却若无其事的人。未曾深夜痛哭过的人，不足以谈论人生。','','\0','2021-09-13 15:49:44'),(81,'生活就是这样，在你看来不过是微乎其微的一个举措，碾碎的，却是别人穷极一生追逐。','','\0','2021-09-13 15:51:01'),(82,'成长就是你最终会发现世界上什么人都有，当你能够豁达地面对他们，拥有制怒之力，不再妄想强行改变谁，那将会是自己的新生。','','\0','2021-09-14 10:09:41'),(83,'人生，总有不期而遇的温暖，和生生不息的希望。','安东尼','\0','2021-09-17 18:00:42'),(84,'越往前走越发现，要与温暖的人在一起，她给你能量，给你时间，让你觉得自己在这个世界的隆重。','雪小禅','\0','2021-09-17 18:03:54'),(85,'我们都是风雪夜中的赶路人，因相遇摩擦，融化了彼此肩头的雪花。','林夕','\0','2021-09-17 18:04:28'),(86,'莲花开在污泥中，人才出在贫寒家。','','\0','2021-09-17 18:05:11'),(87,'每一滴泪水，都会伴随醒悟。每一次伤痛，都是成长的支柱。人生路的风雨，不是为了让你退缩，而是为了让你学会自己撑伞。','','\0','2021-09-17 18:06:19'),(88,'人生有如一股奔流，没有暗礁，激不起美丽的浪花。','罗曼·罗兰曾','\0','2021-09-17 18:06:52'),(89,'天空暗到一定程度，星辰就会熠熠生辉。','查尔斯·比亚德','\0','2021-09-17 18:07:31'),(90,'我们都把自己的鲜血、汗水和眼泪倾注到我们所爱的东西上。','','\0','2021-09-18 20:00:40'),(91,'伟大的背后都是苦难。','罗曼·罗兰','\0','2021-09-18 20:03:34'),(92,'宝剑锋从磨砺出，梅花香自苦\r\n','','\0','2021-09-18 20:03:03'),(93,'没有伤痕累累，哪来皮糙肉厚，英雄自古多磨难。','','\0','2021-09-18 20:04:13'),(94,'你今天敢做别人不敢做的事，明天才能拥有别人不能拥有的东西。','','\0','2021-09-19 16:13:24'),(95,'所有人的坚强，都是柔软生的茧。','','\0','2021-09-22 10:21:02'),(96,'你之所以看不见黑暗，是因为无数勇敢的人把黑暗挡在了你看不见的地方。——《湄公河行动》','','\0','2021-09-22 17:34:34'),(97,'一个人的真正伟大之处就在于他能够认识到自己的渺小。','保罗','\0','2022-02-05 23:45:29'),(98,'不要生气，要争气！','','\0','2022-03-26 14:43:06'),(99,'起点不重要，你去哪里才重要。','','\0','2022-03-26 14:48:01'),(100,'把你的困难当做一个难得的经验，忍耐一时之痛去体会它，你将会因为这些苦难而比别人更加了解人生！','','\0','2022-03-26 14:50:21'),(101,'当你开始真正爱自己，你就不会再为明天而忧虑。','','\0','2022-03-26 15:37:43'),(102,'英雄者，胸怀大志，腹有良策，有包藏宇宙之机，吞吐天地之志者也。','《三国演义》','\0','2022-03-26 15:53:12'),(103,'人和人之间的悲欢是不相连的，有时候你拼了命去追求的东西，可能别人一点兴趣都没有。别人崇拜的信仰，在你这里可能一文不值。目标不同，圈子不同，就连攀比也没有意义。','老窦说车','\0','2022-03-28 10:53:15'),(104,'看一个人成不成功，不是看他现在什么位置，而是看他距离出发的位置有多远。','老窦说车','\0','2022-03-28 10:55:36'),(105,'成功的含义，不是让你超越哪个人，站在什么位置，而是现在的自己超越以前的自己有多少。','老窦说车','\0','2022-03-28 10:55:45'),(106,'据说，曾有人路过梵高的画室，一看见他的《星空》就问，你会画画吗，你有在哪里见过比星辰还要高的树？梵高埋头绘画，半天才答到：你是不知道，用树触摸星辰，从来都是大地的渴望。','','\0','2022-04-02 12:40:19'),(107,'命运压不垮一个人，只会使人坚强起来。','','\0','2022-04-03 20:18:05'),(108,'毛泽东曾说过，鲁迅是个硬骨头，可是一个人不可能一生下来就是个硬骨头。肯定是他的经历塑造了他的思想和态度。','','\0','2022-04-08 21:09:12'),(109,'宁愿独处也不要和融不进去的人待在一起，不要刻意的去维持关系让自己越来越虚伪。宁可孤独，也不违心。','','\0','2022-05-01 18:22:04'),(110,'请允许别人比我们优秀。','','\0','2022-05-03 19:30:47'),(111,'不妨大胆点去生活，其实你没那么多观众。','','\0','2022-05-03 19:32:27'),(112,'不要后悔对任何一个人好，哪怕是看错人，哪怕是被辜负，哪怕是撞南墙。因为，你对他好，不代表他有多好，只是因为你很好！','稻盛和夫','\0','2022-05-07 18:28:24'),(113,'人活着发自己的光就好，不要去吹灭别人的灯。','稻盛和夫','\0','2022-05-07 18:31:23'),(114,'悲惨爱情经历造就强大内心。','','\0','2022-05-09 10:03:38'),(115,'成年人的世界里，谁不是心里装满了故事，眼里写满了心酸，笑里藏着不心甘？还是努力做一个热爱阳光的人，如果不能温暖别人，也可以在寒风刺骨时温暖自己。','','\0','2022-05-09 10:11:28'),(116,'越是不顺的时候越要沉住气，艰难的路不是谁都有资格走，扛得住涅槃之痛，才配得上重生之美。','','\0','2022-05-09 10:47:47'),(117,'世界上没有两片完全相同的叶子，也没有两个完全相同的人！有些事，经历过，有些人，相遇过，那些深深浅浅的人生痕迹，是残留在沧桑岁月里的精美诗句，成为我们生命中的经典!','快乐就好','\0','2022-07-14 01:55:33'),(118,'世界上没有两片完全相同的叶子，也没有两个完全相同的人！有些事，经历过，有些人，相遇过，那些深深浅浅的人生痕迹，是残留在沧桑岁月里的精美诗句，成为我们生命中的经典!','','\0','2022-07-15 15:57:04'),(119,'美好的一天从欣赏，善待，赞美，学习，理解，包容和感恩开始！欣赏别人是一种境界，善待别人是一种胸怀，关心别人是一种品质，理解别人是一种涵养，帮助别人是一种快乐，学习别人是一种智慧，包容别人是一种能力，借鉴别人是一种收获，赞美别人是一种支持，理解别人是一种宽容，感恩别人是一种升华！学最好的别人，做最好的自己，生活才辉煌！','','\0','2022-07-15 15:57:20'),(120,'正气能驱邪，无私可服神。','','\0','2022-08-07 07:37:44'),(121,'一个人能否成才，不决定于名校和名师，也不决定于学历和学位的高低，而决定你自己。具体的来说就决定你自己的理想，和执着追求理想的精神。','','\0','2022-08-07 07:38:25');

/*Table structure for table `article` */

DROP TABLE IF EXISTS `article`;

CREATE TABLE `article` (
  `id` bigint unsigned NOT NULL COMMENT '主键',
  `user_id` bigint unsigned NOT NULL DEFAULT '0' COMMENT '作者ID',
  `username` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '用户名',
  `is_deleted` bit(1) NOT NULL DEFAULT b'0' COMMENT '逻辑删除：(0 正常；1 已删除)',
  `publish` char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '1' COMMENT '是否发布：（0 草稿；1 发布）',
  `recommend` char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '0' COMMENT '是否推荐：(0 正常；1 推荐)',
  `title` varchar(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '文章标题',
  `category_id` smallint NOT NULL DEFAULT '0' COMMENT '文章分类ID',
  `category_pid` smallint NOT NULL DEFAULT '0' COMMENT '文章父分类ID',
  `master_name` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '经历主人姓名',
  `view_count` int unsigned NOT NULL DEFAULT '0' COMMENT '浏览总数',
  `master_url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '经历主人头像URL',
  `avatar` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '作者头像URL',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `idx_category_id` (`category_id`),
  KEY `idx_user_id` (`user_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci COMMENT='文章表';

/*Data for the table `article` */

