/*Table structure for table `article_content` */

DROP TABLE IF EXISTS `article_content`;

CREATE TABLE `article_content` (
  `id` bigint unsigned NOT NULL COMMENT '文章ID',
  `is_deleted` bit(1) NOT NULL DEFAULT b'0' COMMENT '逻辑删除：(0 正常；1 已删除)',
  `content` mediumtext NOT NULL COMMENT '文章内容',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='文章内容表';

/*Data for the table `article_content` */

insert  into `article_content`(`id`,`is_deleted`,`content`,`create_time`,`update_time`) values (1525011574368567298,'\0','<p>个人经历同步微信公众号【经历网】</p><p><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/24/99dabbf7ce584c52b351dfa2823dfd0a.jpg\" alt=\"\" title=\"\" width=\"300\" data-display=\"inline\"></p><p><br></p><p><strong>写正文之前，先简单的说一说，为什么要书写个人经历。</strong></p><p><br></p><p><strong>1、方便社交</strong></p><p>一次书写，以后不用反复向别人介绍自己，一劳永逸；</p><p><br></p><p><strong>2、方便寻人，方便同行、亲朋好友、子孙后代查阅</strong></p><p>无论我们是否轰轰烈烈的活过，都实实在在存在这个世界，来过这个世界。总有一些人会想念我们、关心我们、想了解我们、想联系我们！总有一些人是我们想念的、关心的、想了解的、想联系的！</p><p><br></p><p><strong>3、相互分享，感受不同的人生</strong></p><p>......</p><p><br></p><hr><p><br></p><p><strong><span style=\"font-size: 18px\">如何书写和投放个人经历</span></strong></p><p><br></p><p><strong>一、提交个人形象照片</strong>（1到多张）<strong>；</strong></p><p><strong>二、填写个人基本信息</strong>（根据个人意愿填写）</p><p>中文名</p><p>外文名</p><p>别 名</p><p>性别</p><p>国 籍</p><p>民 族</p><p>籍 贯</p><p>出生地</p><p>出生日期</p><p>星 座</p><p>血 型</p><p>身 高</p><p>体 重</p><p>爱 好</p><p>喜欢的作品</p><p>毕业院校</p><p>政治面貌</p><p>宗教信仰</p><p>座右铭</p><p>口头禅</p><p><br></p><p>主要成就（有则写，无则写自己目前做得最满意的事情。个人作品，请注明：作品名称、创作或出版或发表时间、刊物；剧本，请注明：作品名称、发表时间、制片厂；影视，请注明：作品名称、上映时间、担任职务。）</p><p><br></p><p>职 业 （从事过的所有职业）</p><p>职 称 （目前所从事的职位）</p><p>代表作品</p><p>事业财富</p><p>所持QQ号码</p><p>微信号</p><p>联系电话</p><p>家庭成员</p><p>父 亲</p><p>母 亲</p><p>配 偶</p><p>健康情况</p><p>商会任职</p><p>组织任职</p><p>（其它内容，请自己补充）</p><p><br></p><p><strong>三、人物经历</strong></p><p>根据个人意愿填写，按照年龄增长年月日，记录下自己从小到现在经历过的难忘的、特别的、有意义的事情。以下内容仅供参考：</p><p>1）家庭情况</p><p>2）教育经历</p><p>3）生活经历</p><p>4）工作经历</p><p>5）成功案例</p><p>6）失败教训</p><p>7）婚姻经历</p><p>8）社会活动</p><p>9）获奖记录</p><p>10）个人荣耀</p><p>11）个人成就</p><p>（如果还想记录其它内容，请自己补充）</p><p><br></p><p><strong>四、人物观点</strong></p><p><strong>五、自我评价</strong></p><p><strong>六、社会评价</strong> （他人如何评价您）</p><p><br><br></p><p><strong><span style=\"font-size: 18px\">提交个人经历方式：</span></strong></p><p><br><span style=\"font-size: 14px\">1、请<span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\">将以上资料发送至电子邮箱：908866805@qq.com，标题为《投放个人经历》；</span></span></span></p><p><span style=\"font-size: 14px\">2、请添加微信/QQ： 908866805 提交；</span><br></p><br><p>如果写作有困难，也请通过以上联系方式获得帮助。</p><p><br></p><p><span style=\"font-size: 14px\">个人经历一个自然年可修改两次，首次提交修改到满意为止。</span></p><p><span style=\"font-size: 16px\"><br></span></p><p><br></p><p><strong><span style=\"font-size: 16px\">欢迎您免费投放个人经历！人人可投，只要合法，来稿不拒！—— 经历网</span></strong></p><p><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/24/99dabbf7ce584c52b351dfa2823dfd0a.jpg\" alt=\"\" title=\"\" width=\"300\" data-display=\"inline\"> <br></p><p><br></p><p><br></p>','2022-05-13 15:14:27','2022-08-11 09:43:43'),(1525346273267875842,'\0','<table><tbody><tr><th><p>中文名</p></th><th colspan=\"2\"><p>亚卿俊</p></th><th><p>毕业院校</p></th><th colspan=\"2\"><p>云南大学</p></th></tr><tr><td><p>别 名</p></td><td colspan=\"2\"><p>亚靖华</p></td><td><p>职 业</p></td><td colspan=\"2\" rowspan=\"2\"><p>昆明市怡雅阳光酒店客房部经理、上海市浦东新区昌硕科制造部职工</p></td></tr><tr><td><p>性别</p></td><td colspan=\"2\"><p>男</p></td><td><p><br></p></td></tr><tr><td><p>国 籍</p></td><td colspan=\"2\"><p>中国</p></td><td><p>主要成就</p></td><td colspan=\"2\" rowspan=\"2\"><p>陆良县板桥镇一中三好学生 初级中学期末考试一等奖</p></td></tr><tr><td><p>民 族</p></td><td colspan=\"2\"><p>汉族</p></td><td><p><br></p></td></tr><tr><td><p>出生地</p></td><td colspan=\"2\" rowspan=\"2\"><p>云南省曲靖市陆良县</p><p>搬桥镇大桥村委会</p></td><td><p>代表作品</p></td><td colspan=\"2\"><p>散文《一样的天空》</p></td></tr><tr><td><br></td><td><p>组织任职</p></td><td colspan=\"2\"><p>中国共青团团员</p></td></tr><tr><td><p>出生日期</p></td><td colspan=\"2\"><p>1988年8月27日</p></td><td><p>所持QQ号码</p></td><td colspan=\"2\"><p>3353191090</p></td></tr><tr><td><p>家庭成员</p></td><td colspan=\"2\"><p>父母、一哥 、一妹</p></td><td><br></td><td colspan=\"2\"><br></td></tr><tr><td><p>父 亲</p></td><td colspan=\"2\"><p>亚健民</p></td><td><br></td><td colspan=\"2\"><br></td></tr><tr><td><p>母 亲</p></td><td colspan=\"2\"><p>方美萍</p></td><td><br></td><td colspan=\"2\"><br></td></tr></tbody></table><p><br><br></p><p><strong><span style=\"font-size: 18px\">人物经历</span></strong></p><p><br></p><p><strong>早年时期</strong></p><p>八岁开始挑水做饭，周末寒暑假帮父母干农活，家庭和睦，梦想成为一名科学家，受到玲居、同学、朋友、亲戚敬仰和爱戴。</p><p><br></p><p><strong>中年时期</strong></p><p>2015年1月15到上海浦东新区康桥镇昌硕科技做手机组装。</p><p><br></p><p><strong>社会活动</strong></p><p>2022年4月在上海市浦东新区做志愿者</p><p><br></p><p><strong>获奖记录</strong></p><p>1999年云南省陆良县作文竟赛一等奖</p><p><br></p><p><strong>个人贡献</strong></p><p>参加1998年云南省曲靖市陆良县抗洪救灾</p><p><br></p><p><strong>社会评价</strong></p><p>为人正直，善于帮助他人，热爱公益事业</p><p><br></p>','2022-05-14 13:24:25','2022-08-10 00:42:59'),(1526575351759638530,'\0','<table><tbody><tr><th><p>姓名</p></th><th><p>赵友信</p></th><th><p>配偶</p></th><th colspan=\"2\"><p>李润香</p></th></tr><tr><td><p>性别</p></td><td><p>男</p></td><td><p>籍贯</p></td><td colspan=\"2\"><p>辽宁省盖州市</p></td></tr><tr><td><p>国籍</p></td><td><p>中国</p></td><td><p>出生日期</p></td><td colspan=\"2\"><p>1949年8月22日</p></td></tr><tr><td><p>民族</p></td><td><p>汉</p></td><td><p>简历</p></td><td colspan=\"2\"><p>1958年至1968年 学生</p></td></tr><tr><td><p>身高</p></td><td><p>181cm</p></td><td><p><br></p></td><td colspan=\"2\"><p>1968年 下乡、农民、开了九年拖拉机</p></td></tr><tr><td><p>爱好</p></td><td><p>文娱</p></td><td><p><br></p></td><td colspan=\"2\"><p>1980年 丝绸厂上班</p></td></tr><tr><td><p>职业</p></td><td><p>退休工人</p></td><td><p><br></p></td><td colspan=\"2\"><p>2009年 退休</p></td></tr></tbody></table><p><br></p><p><br></p><h3><span style=\"font-size: 18px\">赵友信的自学成才文娱之路</span></h3><p><br><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>赵友信孩童时期就爱好文娱，那是文化大革命时期，各单位、学校、团体、农村生产队都有文娱宣传队，演出活动很多，几乎每天都有演出，赵友信每次都看，人家在正面看节目，他在旁边看乐队，偷艺。因为那时候他们家附近没有会乐器的，他除了偷师，就是自学。一开始学吹笛子，因为笛子便宜，几毛钱买个笛子。后来看笛子用的人少，二胡用的人多，就想学拉二胡。那时候买一把二胡十多元，赵友信家里没钱，就自己下功夫做，自己做了一把二胡，可是拉出来的声音不好。后来几毛钱买了一本书，他边看书边研究歌谱，二胡学会了，简谱也会了。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>赵友信四十多岁和朋友们一起玩乐队，直到五十岁的时候，才买了自己人生中的第一把二胡，他终于有了真正的二胡，那是一位不是近交的朋友打麻将输了，把二胡卖给他的。朋友要价150元，他就给朋友150元，没有还价，因为他喜欢。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>赵友信说，好二胡都上万元，别人上万元的二胡他拉过，声音好听，比他的二胡声音好听多了；乐器中，提琴是价格最高的，大提琴价格有上千万的。他听过一个新闻，一把价值七千万的大提琴损坏了；国家博物馆里有一把小提琴，已经四百多年了，经过好几个世界上的大师级别的乐师用过，价值连城。这把小提琴，也是世界名师制做的；而他自己做的乐器，一分钱不值，没人要。材质不一样，出来的声音质感不同，紫檀木的二胡都是上万元。紫檀木价值很贵，论克卖。二胡的材质不一样，价格不一样。紫檀木的手串，一粒都几十块钱。紫檀木非常稀少，出产印度，树长的很慢，木质细腻，份量很重，所以音质好听。蟒皮也有讲究，弦也是很重要，弓，弓毛也是很重要。一把好二胡，哪个地方都是上品材料。他听说有几十万元的二胡，几千块钱的都不是很好，他这把150元的二胡，比现在上千块钱的都好。这把二胡年龄不小了，可能跟他的年龄一样。赵友信说，乐器是奢侈品，贵的东西必然好，日子过得不好的人，不能去玩，玩，得有资本。他们一起玩的吹笛子的人，一个月退休金八千多，一个笛子都上千块钱。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>2008年，赵友信他们的大队成立了一个乐队，大队买的大提琴，2800元，最便宜的，当时好的大提琴要上万元。乐队里缺少大提琴手，于是赵友信学了大提琴。自从赵信友拉大提琴后，好几个乐队邀他进去。乐队没有工资，乐队里的人都是退休人员，有退休金。基督教教会成立，教会出场地让他们去玩乐队和排练，夏天有空调，冬天有地热，教会需要乐队时，让他们上台表演，不给演出费，只给点礼物。赵友信所在的乐队是他们全市水平最高的，水平不行的不要，水平比较好的才能进乐队，别的乐队可没有这个排场。赵友信还参入别的乐队、公园、广场等地方玩，他也唱歌，主要是民歌，也有戏剧。</p><p style=\"text-align: center;\"><br><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/46222cedfa45479cbf548c97d1ffc6cd.jpg\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/131948ff85e946b1bb31766f6c74f7d3.jpg\" alt=\"\" title=\"\" width=\"400\" data-display=\"inline\"></p><p><br></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/2749118283ca4df6858887c2ccbc36cf.jpg\" alt=\"\" title=\"\" width=\"200\" data-display=\"inline\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/86f26c8018ab47d1b6e5b81a39e534d8.jpg\" alt=\"\" title=\"\" width=\"200\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/5ccd11c7d0ad471297971bdb51438304.jpg\" alt=\"\" title=\"\" width=\"200\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/1dbd4ba019be44f2bffe3d366095279b.jpg\" alt=\"\" title=\"\" width=\"200\" data-display=\"inline\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/a37fac47294f4445a65e6659f51c14ca.jpg\" alt=\"\" title=\"\" width=\"200\" data-display=\"inline\"></p><p style=\"text-align: center;\"></p><p style=\"text-align: center;\"><br><br><br></p>','2022-05-17 22:48:20','2022-08-21 08:00:05'),(1528412502750703617,'\0','<table><tbody><tr><th><p>中文名</p></th><th><p>陈仕春</p></th><th><p>配偶</p></th><th colspan=\"2\"><p>司徒瑞芳</p></th></tr><tr><td><p>性别</p></td><td><p>男</p></td><td><p>家庭成员</p></td><td colspan=\"2\"><p>妻子、两个儿子、1个女儿</p></td></tr><tr><td><p>国 籍</p></td><td><p>中国</p></td><td><p>职 业</p></td><td colspan=\"2\"><p>画炭相、人像、油画、水粉画、书法、壁画等</p></td></tr><tr><td><p>民 族</p></td><td><p>汉族</p></td><td><p>代表作品</p></td><td colspan=\"2\"><p>纯手工炭相</p></td></tr><tr><td><p>籍 贯</p></td><td><p>广东阳江</p></td><td><p>主要成就</p></td><td colspan=\"2\"><p><span style=\"font-size: 14px\">春<span style=\"font-family: Arial, sans-serif\">記</span>画廊</span></p></td></tr><tr><td><p>出生日期</p></td><td><p>1955年1月15日</p></td><td><p><br></p></td><td colspan=\"2\"><p>阳江市美术家协会会员</p></td></tr><tr><td><p>联系电话</p></td><td><p>13622410603</p></td><td><p><br></p></td><td colspan=\"2\"><p>广东省老干部书画诗词摄影家协会会员</p></td></tr></tbody></table><p><br><br></p><h3><span style=\"font-size: 18px\">人物经历</span></h3><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>陈仕春，男，1955年1月15日生，广东省阳江县岗列公社大㙟大队第十生产队人（当时阳江是一个县，后来才升级为市），爷爷是商人，父亲是村大队干部主办会计，服务于司㙟、新㙟、大㙟、对岸几个大队，陈仕春的母亲是家庭主妇，陈仕春家里条件还算可以。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>陈仕春天生痴迷画画，刚学会走路，就喜欢捡地上的一些白色瓦片在村里的水泥地塘地面上画画。那个时候，陈仕春还没有上学，没有粉笔，村里的人看到他喜欢画画，画得很像，经常会捡一些白色瓦片给他，让他画鸡、猪、兔仔、牛......，画完之后，村里人看了都赞不绝口。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>陈仕春上小学一至三年级，画简单的水彩画，如苹果、香蕉、水杯、洗口盅、牙膏、牙刷等。四年级至初中阶段有所进步，那是文化大革命时期，画毛泽东头像、献忠图案、四大美女图、天女散花、嫦娥奔月、红色娘子军等之类的画。到初中阶段画花鸟画、山水画。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>陈仕春7岁那年，母亲36岁，因被父亲传染了肺病，经常吐血，最后无药可治去世。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1966年，中国共产党还在开展四清运动，在农村中“清工分，清账目，清仓库和清财物”，由于陈仕春的父亲盖了一栋七百元的房子（当时的七百元相当于2022年的十万元或八万元），负责四清的工作人员查看了陈仕春父亲的材料，认为陈仕春的父亲靠本职工资没有经济能力盖房子，子女也还不会赚钱，钱财来路不明，于是将陈仕春的父亲判为贪污干部，没收了他盖的房子，并将他批斗和关禁。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>陈仕春父亲被关禁期间，陈仕春的爷爷做生意，自己独自生活，陈仕春和两个哥哥一个妹妹跟奶奶一起生活。陈仕春的奶奶养猪、鸭、鹅，陈仕春到河里抓些小鱼小虾，从村里拿到县市场上卖，他们以此来维持生活、上学，和攒钱。后来攒到一些钱，分两次付款，把被大队没收了的他们的房子赎了回来，先是花350元钱赎回房子的大厅，过几年再花350元赎回其它房间。那个时候，鸭子三毛钱左右一斤，卖一只鸭子赚一块几毛钱，买一只鸡蛋三分钱或五分钱。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>陈仕春上学，穿的都是打满补丁的衣服，很少换衣服，早上四点钟，天还没亮，就光着脚走到学校教室点油灯上早读。那个时候，孩子们上学，都是比谁来学校来得最早，来得最早的把名字写在黑板上，很开心。当时，陈仕春用的油灯是用墨水瓶做的，往墨水瓶盖上钻一个孔，放一条棉绳或布条进去点着来用。家庭条件好点的孩子，能穿上木屐，用上玻璃罩油灯。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>陈仕春所在的学校，一个学期会组织学生两到三次集体去看电影，去看电影的时候，与陈仕春同龄的孩子，手上有五毛钱，就坐车坐渡船去，买各种好吃的吃。陈仕春手上有两毛钱，舍不得花5分钱坐渡船，自己脱光衣服，放到草帽里，一只手顶着草帽不让衣服弄湿，一只手游泳过河，再走远路过去，他把钱留着买画纸和画笔，他饿着肚子去又饿着肚子回来。实在没有钱买画纸，清明节的时候，陈仕春去偷坟头上的白纸来当画纸。过年过节的时候，武狮和赛龙舟很新鲜，大家都去围观，同龄的孩子也叫陈仕春一起去看，陈仕春不去，专心留在家里画画。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>陈仕春的父亲被关禁了三年，三年间，陈仕春朝、午、晚给父亲送饭，饭菜需要送来的人自己先吃过，才能送到犯人手中。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>三年后，陈仕春的父亲被放出来，找不到好工作，因为有知识有文化，就在外面给别人说书，讲三国演义之类的故事，一天收入一块几毛钱。其它时间，给鱼网铺织鱼网。当时，织一张鱼网可赚十元或八元。陈仕春的父亲不让陈仕春画画，文化大革命时期，画花、画花花世界属于犯法，陈仕春的父亲生怕陈仕春画画出事，和以后连吃饭的钱都赚不到，更没有发展前途，于是看到陈仕春画画就打他。可陈仕春偷偷画，坚持画。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1969年，陈仕春14岁，有一天，陈仕春的奶奶，做了顿黄泽鱼吃，陈仕春的父亲吃了，导致肺病骤然恶化，吐了几次血，经抢救无效去世。从此，陈仕春四兄妹无父无母，跟奶奶相依为命。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>陈仕春上完初中二年级，家里实在困难，没再去上学，投身到生产队工作。由于陈仕春年龄较小，会写字，生产队让他当记分员。记分员工种记7分。其它工种，如插秧、种田、种红薯之类比较粗重的活，记10分，更重的活记14分等。他的工作就是负责记录大家的出勤情况，和所做工种的得分。陈仕春忙完工作就学画油画，一直学。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1976年10月，改革开放，没有了集体生产队，田地分工落户到个人，陈仕春兄妹四人各自都分到田地，陈仕春奶奶已不在人世。陈仕春在给邻居盖房子的建筑工人的介绍和带领下，给两处工地画床花、屋花。附近的人看到了，觉得陈仕春画得好看，就都叫他画床花，或翻新。当时，床上四件套画花收费10元，包吃包住，画一天。第二天，陈仕春就去给另外一户人家画床花。附近画完，没人再过来找他画，他就骑车到人多的地方歇脚，抽口烟，有人问他是干什么的，他说他是画床花的，就又有人给他介绍工作。如果实在没有人给他介绍工作，他就主动去问别人需不需花床花。这样的生活维持了五年，人们的房屋布局有了改变，需要画床花的人越来越少，陈仕春开始转学画人像。手上没有别人的照片，陈仕春拿明星贴纸对照着学画人像，画过张曼玉、刘松仁、张国荣的画像。之后，在阳江开店，画镜画，做了二年。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1980年11月，陈仕春跟朋友上惠阳县淡水镇油家俬包画画，工作了三年。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1983年，陈仕春到惠阳淡水工作的第三年，他赚到钱，成为了那个年代名副其实人人羡慕的万元户，他觉得自己年龄不小了，拿钱回家盖房子，准备娶妻、生孩子。陈仕春回到家里，有人看到他，叫他画床花，他就去画。陈仕春的未来岳母看中陈仕春能画一手好画，将自己的女儿许配给了他，陈仕春很喜欢。1983年年末，陈仕春新居落成，同年11月24日，陈仕春与妻子司徒瑞芳完婚。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1984年1月，陈仕春新婚后的第1个月，妻子已怀孕，留在家里务农，陈仕春和五位朋友商量好，与珠海一食品公司合作开镜画社，他们五人出艺术，食品公司出资金和场地，收益六四分成，他们五人拿六成，食品公司拿四成。同年，陈仕春的大儿子出生。陈仕春在珠海一干就是三年，后来觉得分钱的人太多，拿到手的钱太少，赚不了多少钱，就离开了珠海，回家。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1986年，陈仕春回到家乡阳江，在街道摆摊画布画。同年，陈仕春的二女儿出生。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1987年，陈仕春到东莞太平街边摆摊，画布画，现画现卖。一年后，回家。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1989年，陈仕春的三儿子出生。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1990年9月9日重阳节，陈仕春看完风筝，第二天，正式开始在南恩路街边摆摊画人像，画油画，写书法。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>2019年，陈仕春的一位朋友，非常看好陈仕春的作品，推荐他申请加入阳江市美术家协会，陈仕春提交了申请书和自己的画作，其作品通过了阳江市美术家协会的审核，陈仕春正式加入阳江市美术家协会，成为会员。随后，陈仕春加入广东省老干部书画诗词摄影家协会。协会举办活动，会通知陈仕春参加。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>同年，国家提倡美化城市，不再允许街边摆摊，陈仕春就租了南恩路现在的店面，挂上了春<span style=\"font-size: 14px\"><span style=\"font-family: Arial, sans-serif\">記</span>画廊</span>的招牌，彻底告别摆摊生涯，直到如今。</p><p><br><br></p><h3>陈仕春的艺术人生体会</h3><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>认为从事艺术行业，是一种非常愉快的工种，使自已心情舒暢，又能在社会上有一种独特艺术的贡献感，美化环景，美化室内、室外、公共场所。使人尊敬，令人欣赏，心平气和，没有烦脑，轻松自如，使自已有一种坚強不克，克苦耐劳的感党。如果画好一幅自已认为是最理想的画，很有陶醉感，自豪万分，越画越有劲，使整个人精神振奋，无可比拟。</p><p><br><br></p><p><strong><span style=\"font-size: 18px\">陈仕春作品</span></strong></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/c90ab92b92d64dff9a91b2efa08a8673.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/6bbd35260c944fe989c69a21b8945cca.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/9ac5dd56ca4546fba345094db9d44533.jpg\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/9b41d0b177d1454290136fea3fc0934c.jpg\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/85cb8ad4f820435fbbcaa600fd549d90.JPG\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/4364ef88a11c475889a2965ef960313f.JPG\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/e0e002adc17f497c8b741f686453dc06.jpg\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/a036e0fe17454b51ac16ef463b0761f2.jpg\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/b4327039a4de4750bca451d04d252f32.jpg\" alt=\"\" title=\"\" width=\"200\" data-display=\"inline\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/66beeb42dfbb44b8b79a0c8da6e7c8f3.jpg\" alt=\"\" title=\"\" width=\"200\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/bfd1b0a2a7fa4566abaec3c09cc7d4f9.png\" alt=\"\" title=\"\" width=\"200\" data-display=\"inline\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/9ebb94331d034b978a73eacfa330576a.jpg\" alt=\"\" title=\"\" width=\"200\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/60cf07172118454e8358f729cd13de65.jpg\" alt=\"\" title=\"\" width=\"200\" data-display=\"inline\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/90ea3ec7a8e94651a7728dff413a70be.jpg\" alt=\"\" title=\"\" width=\"350\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/20903e4e24694a9cb1151d495eca042d.jpg\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"></p><p style=\"text-align: center;\"></p><p><br><br><br><br><br></p>','2022-05-23 00:28:31','2022-08-21 08:12:04'),(1529049823250526209,'\0','<table><tbody><tr><th><p>中文名</p></th><th><p>贺先勤</p></th><th><p>身 高</p></th><th colspan=\"2\"><p>175cm</p></th></tr><tr><td><p>性别</p></td><td><p>男</p></td><td><p>体 重</p></td><td colspan=\"2\"><p>66kg</p></td></tr><tr><td><p>国 籍</p></td><td><p>中国</p></td><td><p>职业</p></td><td colspan=\"2\"><p>水电装修工</p></td></tr><tr><td><p>民 族</p></td><td><p>汉</p></td><td><p>家庭成员</p></td><td colspan=\"2\"><p>妻子、两个儿子、孙子</p></td></tr><tr><td><p>籍 贯</p></td><td><p>四川重庆</p></td><td><p>联系电话</p></td><td colspan=\"2\"><p>158 1914 1598</p></td></tr><tr><td><p>出生日期</p></td><td colspan=\"4\"><p>1966年12月26日</p></td></tr></tbody></table><p><br></p><h3><span style=\"font-size: 18px\">人生经历</span></h3><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>贺先勤出生于农村，父母务农，他7岁上小学，成绩一般，为人忠厚、老实、本分，小学毕业后没再上学，在田地里给父母当帮手。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>贺先勤十七八岁的时候，开始到各个村里或到城里给人做棉被，做些手艺活，认识了现在的妻子，他们恋爱了一年多。1989年12日，贺先勤与妻子完婚。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1991年，贺先勤25岁，他看到好多老乡都跑到广东阳江来打工赚钱，他也跟着一起来到了广东阳江。贺先勤的妻子留在重庆家里务农、生养孩子。后来，贺先勤留在阳江。那个时候，贺先勤很年轻，只要他肯干，到哪个建筑工地都能找到工作，挑沙、搬砖，干一些粗重的活，有师傅叫他干水电的活，他就跟着学跟着做，每天赚十几块钱，吃几毛钱一份的快餐，能吃很饱，钱很耐用。贺先勤发了工资，寄钱回家，过年回家。贺先勤一干就是二三十年。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>2014年，贺先勤的两个孩子都已大学毕业，到外面工作，他们嫌工资太低，去了广东惠州做起卖面条的小生意，已结婚生孩子。贺先勤的妻子过来阳江，与贺先勤一起生活，他们租住300元的单间。贺先勤的妻子平时在楼盘里做杂工。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>2022年前后，全球新冠肺炎病毒传染已持续三年，所有人生活变得困难，不知何时好转，不知病毒何时消失，贺先勤工作的机会很少，只有等本地包工头的电话，等到电话才有工作。他也每天站在马路边，等有人叫他去工作。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>贺先勤工作的时候，工地大的话按平方算工资，工作少的话按天算工资，每天赚两三百块人工钱，不耐花，买一个最简单的快餐都要十几块钱。贺先勤所做工地不敢偷工减料，怕以后房子出事他们要承担责任，怕坏了自己的名声，以后再没有人找他干活。现在，贺先勤年纪大了，干一些轻一点的活，做水电装修工。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>大多数工地干完活就结清账，少数好赌的包工头拖欠贺先勤和其它工人的工资，拖欠四五个月，贺先勤和其它工人大发脾气，包工头才给结清工资。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>2022年，贺先勤与孩子们不在同一个城市工作，孩子们在惠州能赚到钱就留在那边，贺先勤习惯了阳江的生活，就继续留在阳江，他和妻子待到孙子要上学的时候，过去照看孙子。过年的时候，贺先勤和孩子们都回重庆老家与亲戚朋友们团聚。贺先勤老家还有房子。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>贺先勤平时爱看新闻，了解时政，他希望我国不要跟其它国家发生战争，他希望世界和平，希望人们都能安居乐业！</p><p style=\"text-align: center;\"><br><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/b87496ea3af4453c939cd27b765ef17c.jpg\" alt=\"\" title=\"\" width=\"400\" data-display=\"inline\"></p><p style=\"text-align: center;\">贺先勤站在马路边等有人叫他去工作。</p><p><br><br></p>','2022-05-24 18:41:00','2022-08-21 08:01:48'),(1529392342157012994,'\0','<table><tbody><tr><th><p>中文名</p></th><th><p>何秋许</p></th><th><p>星 座</p></th><th colspan=\"2\"><p>狮子座</p></th></tr><tr><td><p>性别</p></td><td><p>男</p></td><td><p>身 高</p></td><td colspan=\"2\"><p>160cm</p></td></tr><tr><td><p>国 籍</p></td><td><p>中国</p></td><td><p>体 重</p></td><td colspan=\"2\"><p>60kg</p></td></tr><tr><td><p>民 族</p></td><td><p>汉族</p></td><td><p>爱 好</p></td><td colspan=\"2\"><p>太极</p></td></tr><tr><td><p>籍 贯</p></td><td><p>广东阳江</p></td><td><p>家庭成员</p></td><td colspan=\"2\"><p>妻子、两儿子</p></td></tr><tr><td><p>出生日期</p></td><td><p>1964年06月19日</p></td><td><p>主要成就</p></td><td colspan=\"2\" rowspan=\"2\"><p>阳江二中前校长杨永皓为何秋许送上一幅字\"助人健康为乐\"。</p></td></tr><tr><td><p>毕业院校</p></td><td colspan=\"2\"><p>广东阳江市阳西塘口中学</p></td></tr><tr><td><p>联系电话</p></td><td colspan=\"4\"><p>183 1634 1583</p></td></tr></tbody></table><p><br><br></p><h3><span style=\"font-size: 18px\">人生经历</span></h3><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>何秋许，男，1964年06月19日生，广东省阳西县塘口镇热水乡大塘村人，父母在生产队里务农，父亲懂医术，爷爷开诊所，是广东阳西五大名人之一何恬斋，人称何五公的后代。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>何秋许小的时候，隔壁村有人中风，派人过来找何秋许的父亲去抢救，何秋许总是打着手电筒跟着父亲去救人（那个年代还没有路灯）。何秋许的父亲以烧艾灸穴位把人救好。跟着父亲长期临床实践，何秋许慢慢也学会了烧艾灸穴位救人。可是，何秋许的父亲知其穴位和灸穴顺序，却不知其穴位名。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1980年，何秋许上初二，他认识多一些字后，耐心地查找资料，给父亲的灸穴方法标注上穴位名称和手法顺序。然后，编成顺口溜口诀诗，方便自己记到心里去。譬如，治疗中风的顺口溜：热烧百会到中冲，三烧拳开将界封，四烧齐膀五齐背，六烧尾企出人中....</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>何秋许上初中，放假时，跟父母一起务农，插秧、割禾，干一些力所能及的活来赚工分。村里到学校的山路很难走，骑不了自行车，村里面与何秋许年龄相仿的孩子们，集体自发修路，共同铲锄出一条平整的泥路，方便大家上学和外出。如今这条路已修为水泥路。有很多想美容、养颜、活化全身血液的外地人，专门开车过来热水乡泡热水温泉。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1982年，何秋许初中毕业，他在家里帮父亲卖药、卖膏药、草药。他把自己整理好的父亲的灸穴方法画成艾火图，背后还印有他原创的灸穴方法口诀诗，复印后拿来卖，5块钱一张，他也送一些给要好的同学、亲戚、朋友。可是，很多人看不懂艾火图，不会用，没有珍惜。后来，一位与何秋许工作过的同事，再次向何秋许要艾火图，艾火图已被何秋许卖光送完，炙穴口诀诗也已深入何秋许心里，他不再需要艾火图。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1984年12月，一位3岁小女孩，得了白血病，在阳西医院治疗一段时间后，医院判定没有办法治疗。女孩出院后，女孩的父母亲找到何秋许，问他有没有什么秘方或方法治好自己女儿的病。当时，这个小女孩已经没有了生命的迹象，何秋许说有个祖传的方法可以试试！死马当活马医。结果，何秋许用这个方法治疗好了小女孩的白血病，直到现在38年过去，小女孩也41岁，已结婚生育一儿一女，身体状况佳。这是何秋许人生的第一位案例。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1988年，何秋许的同学找何秋许一起到阳江城里开家具厂，何秋许说没钱，也不会做家具，何秋许的同学说看到过何秋许修理家具，认为他可以。就这样，何秋许的同学出钱，何秋许出力，他们干起卖家具的生意，连干三年，平均月收入五六百元。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1989年，何秋许将赚到的钱，在家里盖了自己的房子，大概花费几千元到一万元。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1991年，何秋许的同学不再开家具厂，何秋许进别的家具厂，继续从事做家具的工作。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1992年8月，何秋许回家结婚，结完婚过中秋节。然后，留在家里自己做家具，拿给别人卖。同年，何秋许有了自己的儿子。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1994年，何秋许的朋友叫何秋许一起去闸波碧桃园做装修，负责做木工，做天花、家具。何秋许进入装修行业，一做就是十年，期间，自己也接工程做，还学会了做电工。平时，何秋许遇到中风的人，或需要急救的人，本能地主动伸出援手，利用自家祖传通经烙的手法把人救过来。之后，有人叫何秋许进“恒大美容”，接受培训，从事帮助别人通经烙调理身体的工作。何秋许虽然从事了装修行业，但在他心里，他是祖传中医，继承和发扬祖传救人医术，才是他真正想要做的事情。于是，他进了“恒大美容”，接受了培训，做了一段时间，感觉都是女性来做美容的比较多，自己给人通经烙的祖传技能，没有发挥的机会，没能派上用场。因此，他离开了恒大。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>1995年，何秋许的二儿子出生。同年，何秋许妻子的舅舅退休，身患多种疾病，中风、扁喉炎、头病、血压高、高血糖，家里到处是药，中风时被何秋许救醒，何秋许根据他的身体情况，分级别把功法教给他，让他坚持练，使他身体大有好转，不用再吃药。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>2004年之后，有一位学佛的夫人，因中风被何秋许救了过来，她为感谢何秋许，把自己马南垌家中的二楼，无偿供给何秋许行医救人，还给何秋许介绍病人。何秋许感受到了这位夫人的真诚，接受了这位夫人的好意。平时，何秋许除了在这位夫人家中二楼给亚健康的人调理身体，还为行动不便的病人和重症患者上门服务，费用每次3到5百元。本省内哪里有人需要他，他就过去那里。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>被何秋许救了的夫人，给何秋许介绍过一位远在香港的病人，这位香港病人，是一位老板，是这位夫人和丈夫到香港参加佛教协会活动时认识的佛友。这位香港老板六十多岁，得了肝癌，到美国花了好几百万也没能治好，后来别人引导他学佛，遇到了这位夫人。何秋许没有过去香港，他隔空为这位香港病人调理身体，先是让他服用这位夫人正在服用的广西硒化螺旋藻。何秋许联系广西硒化螺旋藻生产基地购买，让他们直接发货到深圳一酒店，然后这位香港病人自己或找人过去取。广西硒化螺旋藻，一盒十瓶两千元。何秋许再给香港病人写了一张祖传秘方，其中需要煲牛肺、冬虫夏草。接着，何秋许电话中口授煲药方法和通经烙功法。之后，两人QQ中交流香港病人服药和练功之后的病情进展。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>香港病人服药和练功一段时间后，再去医院检查身体，肝病好转，医生问他怎么调理好的身体，他说大陆有一位医生，教他一种手法，很神奇。至今，这位香港病人坚持练功，身体和精神都很好。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>何秋许的太公何五公寿龄103，父亲93岁，他们祖传养身有一套功法，救人治病也有一套功法。功法结合少林、太极、气功三种功夫，加治病心法要决秘决，达到迅速将体内的病症解决。有的病患者，身体有多种病，去看医生，治好一种病，其它病又发作，练何秋许祖传这套功法，结合何秋许调理穴位的手法，能帮助恢复健康。何秋许拿到了“恢复理疗证书”，看病不开处方，不吃药，根据不同的病症，按顺序调理不同的穴位，他称之为经烙穴位方，是他祖传的秘决，结合练他的功法，达到治病效果。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>2019年，与何秋许一起练太极的师姐的母亲，中风卧床不起，她家亲戚是医生，没能给老太太帮上忙。何秋许的师姐，请何秋许帮忙调理母亲身体，调理过后，老太太会自己下床走路了。之后，何秋许每年为老太太调理一次身体。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>何秋许与这位师姐练太极，师从武当太极第十二代传人 —— 陈国强。陈国强，就职于广东省阳江市公安局110，阳江人，其太极集养生、健身、擒拿、推手结合，他广收徒弟。何秋许在朋友的推荐下，拜陈国强为师。何秋许自小喜欢太极，过年跟着师傅一行人一起出城舞狮，当时教他舞师的师傅也叫陈国强。阳西溪头还有一位太极师傅，亦名陈国强，教陈式太极。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>何秋许调理过距离最远的病人，是一位来自新疆的中年女士。何秋许曾经调理好一位妇人的腰，妇人的腰是被撞伤的。妇人把何秋许，介绍给自己小区里，一位来自陕西，身患四年腰间盆突、增生的保安小伙。何秋许三五分钟，一次性调理好了这位保安小伙的腰间盆突出。保安小伙，把何秋许介绍给了这位来自新疆的中年女士。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>新疆的中年女士，自己是一名医生，毕业于南京医科大学，回到新疆铁道卫生院上班，在检测科。这位女士四十多岁，因病退休，面容憔悴，看上去比实际年龄大很多。她身体有很多问题：头晕、眼花、记忆力差、心脑血管、腰间盆突出、腰肌劳损、月经不正常、白带过多、乳腺有结节、子宫有问题等等。这位女士从新疆坐飞机到重庆，转机到广州，坐班车到阳江。何秋许去接她，考虑到她要在阳江住一段时间，建议她租房，300块钱一个月，租了一个套房，使她安心让何秋许为她调理身体。这个月，何秋许每天为这位女士调理一次身体，只用手法通经烙，烧艾灸穴位，没用药物。一个月后，女士的身体健康起来，女士兴奋的邀请何秋许一起去环游世界，何秋许自知是有妇之夫，非常珍惜自己的妻子，不便同行，一一拒绝。何秋许收取治疗费每天200元，一个月下来6千元，女士外加奖励，共给了何秋许一万元。后来，何秋许家里追盖房子，这位女士把环游世界的钱送给了何秋许当作贺礼，平时自己家里有好吃的，也给何秋许寄过来。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>在何秋许的人生当中，类似这样成功的案例还有很多：调理好一位女子的身体，挽救了她即将破碎的家庭；调理好69岁妇人的心脏病，使她不用去受做心脏支架搭桥的苦；调理好公路局退休老人的身体，使他免受中风不能自理之耻；调理好房东30年的内伤，让他找回中年男人的活力；调理好68岁妇人的风湿，让她恢复走路自如......</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>何秋许曾经也觉得自己的调理手法很神奇，感觉是上天在庇佑，使他从事这个行业越来越有信心。</p><p><br></p><p><span style=\"font-size: 18px\"><span style=\"font-family: -apple-system, Microsoft YaHei, serif\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: rgb(255, 255, 255);\">　　</span></span></span></span>2020年，何秋许阳春的一位朋友来阳江，要介绍何秋许去一个地方学调理经烙，收费3900元，何秋许跟她过去，看到培训班是以拍打经烙的方式来调理身体，何秋许不认可这种调理方式，认为一般人可能不会接受这种调理方式，并且演示了自己祖传的柔性调理经洛手法，在场多人称好，要拜何秋许为师。后来，何秋许留在了朋友的经烙调理店，出手艺、收学徒，负责经营，直至今天。</p><p style=\"text-align: center;\"><br><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/856533eca9924b0da7587a722be4483d.jpg\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"><br><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/321db828629344b3a72d5773868c2d6c.jpg\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"></p>','2022-05-25 17:22:03','2022-08-21 08:03:24'),(1530047351815188482,'\0','<table><tbody><tr><th><p>中文名</p></th><th><p>钟东明</p></th><th><p>籍 贯</p></th><th colspan=\"2\"><p>广东省茂名市电白</p></th></tr><tr><td><p>性别</p></td><td><p>男</p></td><td><p>所在地</p></td><td colspan=\"2\"><p>广东阳江</p></td></tr><tr><td><p>国 籍</p></td><td><p>中国</p></td><td><p>出生日期</p></td><td colspan=\"2\"><p>1959年12月10日</p></td></tr><tr><td><p>民 族</p></td><td><p>汊族</p></td><td><p>联系电话</p></td><td colspan=\"2\"><p>184 2017 8152</p></td></tr><tr><td><p>身高</p></td><td colspan=\"4\"><p>180cm</p></td></tr></tbody></table><p><br></p><p><strong><span style=\"font-size: 18px\">人物经历</span></strong></p><p>　　钟东明，男，1959年12月10日生，广东省茂名市电白区马踏镇人，出生于一个地主家庭，母亲识字、能说会道、会唱歌跳舞、烧艾治病。</p><p><br></p><p>　　文化大革命时期，钟东明的父亲去世，钟东明的母亲带着钟东明和钟东明的哥哥改嫁。钟东明上学的时候写作文《给毛泽东的一封信》获得了马踏镇第一名。马踏镇有20多个学校。</p><p><br></p><p>　　钟东明的哥哥上5年级的时候，边上学，边教书，有空时弹杨琴、拉二胡。钟东明八九岁的时候，大队干部说地主仔太聪明，民兵看到钟东明兄弟俩，就捉他们，开枪打他们。钟东明的哥哥带着钟东明逃到山上去。一天白天，钟东明的哥哥带着钟东明打算下山去投靠熟人，不料途中钟东明的哥哥被其它帮派的人给捉了起来，押回了大队，钟东明逃脱了跑回山岭。钟东明的哥哥在大队里被关了十天，之后被拉出去枪毙了。钟东明在山岭没东西吃，就偷番薯吃，偶尔跑到外婆家拿点米煲粥吃。</p><p><br></p><p>　　那年7月7日，钟东明十几二十岁的时候，中央开会，不再打战，钟东明回家，在生产队里不愿干活，又挨批斗，钟东明逃到广州去，结果被捉住押回了湛江，送回电白，接着挨批斗。后来钟东明又跑了出来，到海南流浪，跟别人一起打些零工度日。</p><p><br></p><p>　　钟东明二十二三岁时，从海南回到家乡，开始学做音乐，弹扬琴，拉二胡，喝粤曲表演赚钱生活。</p><p><br></p><p>　　钟东明二十七八岁时，遇到电白有名的风水师陈立的父亲。陈立的父亲看到钟东明口才好、记忆力好、聪明，就教他易经、佛学、看相、看风水、算命。钟东明学会后，和陈立下乡开档算命，别人收两块钱，他们收五块八块钱。还有一些人，从阳春开车过来找他们算命。</p><p><br></p><p>　　没多久，钟东明认识了一位当老师的姑娘，他们恋爱了，已到谈婚论嫁的地步，姑娘未婚一直守身如玉，钟东明自知家里条件不好留不住这位姑娘，跟她分开了。</p><p><br></p><p>　　后来，一有夫之妇，生有孩子，因为老公身体不好，她看到能说会道的钟东明，非常喜欢，于是隐瞒自己已婚，跟钟东明住一起。一段时间后，妇人的丈夫知道了，过来要打钟东明，钟东明只好跑到阳江平岗一带摆摊给人算命。从早上摆到傍晚，很多人过来排队找钟东明算命、求签、解签，费用一块、两块、三块。</p><p><br></p><p>　　钟东明说话让人很爱听，一离异妇人，为此喜欢上了钟东明，与钟东明登录结婚。婚后，钟东明才知道妇人已结扎，好赌。妇人掌管了钟东明的收入，拿去赌。钟东明受不了，两人经常吵架，妇人睡觉的时候床头还放一把刀，钟东明怕被她砍了，逃离。后来，妇人的母亲遇到钟东明，让他和自己的女儿去把婚离了。钟东明跟妇人离婚后，妇人向钟东明要了6千元分手费。第二年，妇人在别人的怂恿下，又过来找钟东明要钱，钟东明不给，妇人就找一些地皮流氓来威胁和骚扰钟东明，不让钟东明在阳江摆摊，钟东明不得不又给了妇人5千元。那个时候，钟东明的母亲已不在人世，钟东明还有同母异父的弟弟和姐姐。</p><p><br></p><p>　　钟东明的弟弟是教师，姐姐上过四年学，生了四个孩子。钟东明姐姐的这四个孩子，全部大学本科毕业，一个在中山当会计师；一个是华南理工大学毕业的在恒大当总工程师，是恒大老板身边的红人；一个是工程建筑师；一个在东莞开学校，下象棋全广东省第一。过年的时候，钟东明的这几个外甥都会给他几千块钱红包。</p><p><br></p><p>　　钟东明还有一个结拜兄弟，是钟东明师傅的儿子陈立，长得异常俊美，很讨女性喜欢，比钟东明小七八岁，他俩曾经一起开档给人算命、求签、解签。如今，陈立已是大师级别的易经风水相命师，有好车、好房、好儿子，生活、工作、女人缘都比钟东明好很多。钟东明和陈立一直保持联系。</p><p><br></p><p>　　钟东明这些年，一直孤身租住在阳江北山附近的一个单间里，无妻无儿无女，在家里给人算命，或到北山公园遇有缘人相助，或接受他人邀请上门服务，或陪人上佛寺烧香拜佛给人念经。有人问他，既然会算命改命，为何自己的命运还这样悲惨。钟东明道，是自己前世无修，上辈子欠人家的。别人再问，既然你化解不了自己前世今生欠下的债，又如何能帮助别人化解？钟东明双手合十，说自己念佛念了二十多年，每天早上面向西方双手合十念十篇：南无三十六万亿，一十一万九千五佰，同名同号阿弥陀佛。回向三声，此经文可超度历代祖宗，九代升天。三十六万亿，一十一万九千五佰，是指阿弥陀佛的总数量。钟东明继续念道：往昔所造诸恶业，皆由无始贪慎痴。从身语意之所生，一切我今皆忏悔。回向三声，一生忏悔，可消除所有罪孽。</p><p><br></p><p>　　钟东明教人用左手刷牙，喝水的时候，用右手无名指，在水面上写“南无阿弥陀佛”，然后再喝，可消除百病；每月初一十五，用柚子叶泡水洗澡洗头发，可辟邪；每月初一十五，向东方，双手掌向天托三托，再双手掌向地面抚三抚，同时嘴上说，天大地大我也大，我必成功，好运来，讲一些好话，可转运。平时不要说死字、鬼字；多吃素，少吃肉；想要事业成功，屋里一定要有旗、鼓、印、箱、龙、龟、樽、联......；春季出生的人，要补金； 夏季出生的人，要补水；秋季出生的人，要补木；冬天出生的人，要补火，吃红色性温的食物...... 钟东明说自己属佛教，谁遇到他都会开始行好运，每天按照他说的做，日后一定会回来感谢他！他自己，晚年将会在佛寺中度过。</p><p style=\"text-align: center;\"><br><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/508ded1ea95c4107ac174f6b980d060b.jpg\" alt=\"\" title=\"\" width=\"400\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/490c5ea201f440bdb457a26241124dc4.jpg\" alt=\"\" title=\"\" width=\"400\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/be6fd01bcc8747c3891e747d6a5590c5.jpg\" alt=\"\" title=\"\" width=\"400\" data-display=\"inline\"></p><p><br><br><br></p>','2022-05-27 12:44:50','2022-08-21 08:05:08'),(1530757962983804929,'\0','<table><tbody><tr><th><p>中文名</p></th><th><p>雷振</p></th><th><p>身 高</p></th><th><p>170cm</p></th></tr><tr><td><p>性别</p></td><td><p>男</p></td><td><p>体 重</p></td><td><p>78kg</p></td></tr><tr><td><p>国 籍</p></td><td><p>中国</p></td><td><p>爱 好</p></td><td><p>诗词</p></td></tr><tr><td><p>民 族</p></td><td><p>汉族</p></td><td><p>毕业院校</p></td><td><p>私塾（馆子）</p></td></tr><tr><td><p>籍 贯</p></td><td><p>广东阳江</p></td><td><p>家庭成员</p></td><td><p>两儿子四女儿</p></td></tr><tr><td><p>出生日期</p></td><td colspan=\"3\"><p>1934年12月30日</p></td></tr></tbody></table><p><br></p><p><strong><span style=\"font-size: 18px\">人生经历</span></strong></p><p>　　雷振，男，1934年12月30日生，广东省阳江市大沟填将军村人，太公是一名将军，父母务农，家境贫苦。</p><p><br></p><p>　　雷振七八岁的时候，跟村里十来个孩子一起上私塾，学三字经，不上学的时候，进山里割草捡柴回家烧饭做菜。雷振自小爱吟诗作对，有一次他上山割草，一时兴起，作诗一首：出门日日见青山，山色青青不改颜。我问青山何日老，青山问我几时闲。然后，他把自己作的诗都写到一个本子上，写得满满的。</p><p><br></p><p>　　雷振自小胆子大，与生俱来就有保村护民的责任感，隔壁村进来土匪打家劫舍，他和其它人一起去打土匪，</p><p><br></p><p>　　1949年，雷振15岁，自己学会骑马，开始参加革命，主要负责给游击队送信，从东平送到大沟区府。当时，雷振所骑的马，是部队抄了地主的家得来的一匹上乘好马。雷振送一趟信，要5、6个小时，有时会在梁管所等消息，有回信就一起带回来，没回信就空手回来，全程跑山路，途经鸳鸯石，过心丫渡的时候，鸣枪唤人，远处对面岸边的船夫听到枪声，便知道有游击队的人要过河。</p><p><br></p><p>　　部队给雷振配枪，雷振随身携带，他自学开枪，第一次开枪不害怕，后来经常开枪，一来防身，二来用作过渡的通知信号，从没开枪杀过人。</p><p><br></p><p>　　雷振负责送信送了七八年，后来参加工作，被组织分配到阳江市公安局波陵园当看守所当所长，再后来被调到更大的白沙看守所当所长，专门监管犯人。雷振一切行动听指挥，绝对服从组织的安排，看守所所长一职一当就是几十年，月薪从一百多到后来的六七千元，直至60岁退休，现在月领过万的退休金。</p><p><br></p><p>　　雷振在看守所工作，给犯人分配牢房，重犯两三个人一个牢房，普通犯人，十来个人一个牢房。牢房三四十平方米大，水泥地板，有一个窗，里面有用隔板隔开的卫生间和洗澡的地方，80公分高的大通铺木板床，冬天加一床被子；吃饭时间，看守所的人把饭从狗洞一般大的地方放进牢房，一人一盆，菜都是青菜，没什么油，随便犯人吃或不吃；给犯人放风的地方，二十多平方左右，四面高墙，只能看到天空；平时也给犯人发报纸看。</p><p><br></p><p>　　看守所有时有加工塑料的活给犯人做，犯人用自己额外的劳动力赚到的钱，1块钱，犯人拿8毛，其余两毛钱用来给犯人加点菜，加点肥猪肉吃。</p><p><br></p><p>　　新进来的犯人的家属要存一些钱进看守所，给监狱里的监霸头们加菜吃，那样新进来的犯人才不会挨打。</p><p><br></p><p>　　有的犯人实在受不了坐牢的苦，慢慢刮砖准备逃狱，反正也是坐牢，想逃狱的犯人不在乎再次被抓住，只是成功的机会很渺茫。监狱里经常会有犯人偷偷地哭，或吟诗作对，诉尽心中苦。牢房里也会有犯人斗殴，情节严重的，给犯人加刑，给犯人带上脚镣手铐，那样脚会很痛，行动极其不方便。犯人的家属过来探望犯人，隔一张玻璃对话，时长大概十来分钟。犯人家属带来的食物，家属先吃过，看守所的人检察过后，才交到犯人手中，让犯人带回牢房跟其它犯人分吃。过年过节，看守所会给犯人加点菜，无任何娱乐。</p><p><br></p><p><br></p><p><strong><span style=\"font-size: 18px\">婚姻与家庭</span></strong></p><p>　　雷振有一同事，想娶阳西县织篢镇的一位姑娘，姑娘的母亲是街道主任，雷振的同事问姑娘，雷振22岁，他自己19岁，问姑娘喜欢谁。姑娘说喜欢雷振，雷振很开心，因为他也很喜欢这位漂亮的姑娘，雷振的同事很伤心。两三个月后，雷振与这位姑娘结婚，雷振的同事被领导调到了大西北工作。</p><p><br></p><p>　　雷振结婚后，爱人很快就怀孕了，之后雷振工作，爱人在家里生儿育女。雷振共有两个儿子四个女儿，孩子们长大后，一个儿子在外地做生意，其它孩子，都在公安局里工作，孩子们也有了自己孩子。</p><p><br></p><p>　　雷振的爱人四十多岁的时候，在家里中风去逝。之后，雷振一直没再遇到喜欢的人，没再娶。雷振对自己一生的生活、工作、家庭都比较满意。</p><p><br></p><p><br></p><p><strong><span style=\"font-size: 18px\">雷振晚年生活</span></strong></p><p>　　雷振住过两个月民政局养老院，自己可以吃饭走路，每月缴费1千七八百元。那些生活不能自理的老人，月缴费三四千元。</p><p><br></p><p>　　养老院包吃，有肉饼、青菜、豆腐、鸡蛋、米饭、稀饭等，自助餐式，随便吃。老人们平时可到大院子里去散散步晒晒太阳。雷振不喜欢住养老院，感觉不自由，不能经常看到家人；养老院一个房间住几个老人，他跟老人们不怎么交流，有的失忆老人，夜里会脱光衣服走到他床前，让他感到很恶心；有的老人夜里不睡觉，到处乱跑，影响他睡觉......</p><p><br></p><p>　　现在，雷振不再住院养老院，独自住在自己一楼的家里，雷振的儿女给他请了一个五六十岁的男护工，每天二十四小时倍在雷振的身边，照顾雷振的生活起居。雷振的儿女们经常会来看雷振，给雷振煲好汤做好饭菜吃。雷振的女儿还想给雷振找一个老伴，可以照顾雷振的生活，每天陪雷振说说话、陪雷振一起睡觉，让雷振不用这么孤独。雷振的女儿让雷振去公园的时候留意身边的女性，如果有看中的，向女人要电话号码，然后雷振的女儿去跟对方沟通。</p><p style=\"text-align: center;\"><br><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/6c6ca1a224a74e38b0df4f92f4c229f4.jpg\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/18/7387f71cccd34a4099b548649d579d2e.jpg\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"></p><p style=\"text-align: center;\"></p><p style=\"text-align: center;\">坐在轮椅里的雷振喜欢到公园里听老人家讲革命故事、讲当今政治</p><p><br></p>','2022-05-29 11:48:33','2022-08-21 08:06:28'),(1532311192494673921,'\0','<p>　　陈国强，男，汉族，中国共产党员，1964年12月出生于广东省阳江市江城区。现任阳江市公安局指挥中心副主任、一级警长、阳江市公安局前卫体协太极拳俱乐部主任。武当赵堡太极拳第十二代传承人，中国侯氏太极拳第三代传承人，国家二级武术裁判员，武术六段。获武当国际演武大赛组委会授予武当赵堡太极拳名师、武当赵堡太极拳优秀传承人称号，武当赵堡侯氏太极拳阳江地区总教练。陈国强师承武当赵堡太极拳名家张仁、艾学林，是当代中国侯氏太极拳掌门人侯转运先生的入室弟子，得师父秘传的武当太极养生功法，精通武当赵堡太极拳，擅长武当太极剑和道家拂尘。聘任阳江市老干部大学武当赵堡太极拳总教练，并将武当赵堡太极拳在阳江市老干部大学、阳江市公安局、阳江市市委党校、阳江市市政法委、阳江市市总工会、阳江市市妇联、阳江市职业技术学院、阳江市技工学校、阳东第二中学、阳江中国银行、南方电网阳江分公司、中国人寿阳江分公司等机关事业单位和高等院校，积极推广和宣传太极养生之道，深受机关事业单位太极拳爱好者的好评！</p><p><br></p><table><tbody><tr><th><p>中文名</p></th><th><p>陈国强</p></th><th><p>身 高</p></th><th colspan=\"2\"><p>165cm</p></th></tr><tr><td><p>别 名</p></td><td><p>陈强</p></td><td><p>体 重</p></td><td colspan=\"2\"><p>65kg</p></td></tr><tr><td><p>性 别</p></td><td><p>男</p></td><td><p>爱 好</p></td><td colspan=\"2\"><p>武当赵堡太极拳</p></td></tr><tr><td><p>国 籍</p></td><td><p>中国</p></td><td><p>毕业院校</p></td><td colspan=\"2\"><p>广东省江门市五邑大学</p></td></tr><tr><td><p>民 族</p></td><td><p>汉族</p></td><td><p>职 业</p></td><td colspan=\"2\" rowspan=\"2\"><p>人民警察（阳江市公安局指挥中心副主任）</p></td></tr><tr><td><p>籍 贯</p></td><td><p>广东阳江</p></td><td><p><br></p></td></tr><tr><td><p>宗教信仰</p></td><td><p>道教</p></td><td><p>代表作品</p></td><td colspan=\"2\"><p>《韵绕太极魂》《太极古韵》</p></td></tr><tr><td><p>出生日期</p></td><td><p>1964年12月27日</p></td><td><p>主要成就</p></td><td colspan=\"2\" rowspan=\"2\"><p>传承武当赵堡太极，将武当赵堡太极拳在阳江地区发扬光大，惠及阳江机关事业单位及阳江各高等院校</p></td></tr><tr><td><p>联系电话</p></td><td><p>13902528121</p></td><td><p><br></p></td></tr><tr><td><p><br></p></td><td><p><br></p></td><td><p>座右铭</p></td><td colspan=\"2\" rowspan=\"2\"><p>太极是你一生的朋友，太极是你一辈子的财富</p></td></tr><tr><td><p><br></p></td><td><p><br></p></td><td><p><br></p></td></tr></tbody></table><p><br></p><p><br></p><p><strong><span style=\"font-size: 18px\">工作经历</span></strong></p><br><p>　　1989年，陈国强先生毕业于江门五邑大学，他攻读的专业是计算机工程，其计算机软件和硬件技术相当出色，毕业后曾在阳江粤中公司工作，1991年，阳江市政府组建信息中心，他作为计算机专业人才被选调到阳江市信息中心，负责计算机业务，期间，在机关单位事业举办计算机培训班。率先在阳江地区掀起学习电脑的热潮。1993年底，因工作需要，被调到阳江市公安局工作，成为一名光荣的人民警察。先在出入境管理科、又到户政管理科、再到信息通信科担任副科从事计算机管理工作，最后到指挥中心指挥室担任副主任。繁忙的工作之余还是要耍耍太极拳。</p><p><br></p><br><p><strong><span style=\"font-size: 18px\">习武经历</span></strong></p><br><p>　　陈国强先生1964年12月27日出生于广东省阳江市江城区，从小就喜爱武术、太极拳，从1984年春节后开始就跟他外公梁运辉习练杨式太极拳，当初，每天天濛濛亮，就从床上爬起跑到5公里外的中山公园跟着他外公及他外公的弟子一起站桩、压腿、走猫步等等基本功，当时，他外公梁运辉非常严格，凡跟他学太极拳的学员都要先站桩三个月，并且一站就要20分钟以上，记忆最深的是刚站桩时，两腿发抖，豆大的汗滴开始从额头冒出，穿的背心都能拧出水，当晚，酸、胀、痛全都出来了，第二天走路都一拐一拐的，啊，记忆深刻。就这样咬紧牙关熬过半年，将杨式太极拳85式拳架全部学会，因此对太极拳产生了浓厚的兴趣。后来，陈国强先生将这种浓厚的爱好带到了大学校园，一边研读功课、一边练习太极拳，从不间断。</p><p><br></p><p>　　陈国强先生大学毕业，参加工作后，除了保持继续跟外公梁运辉习练太极拳外，还常到各公园拜访各类太极拳老师。1998年某天早上，旭日东升，云蒸霞蔚，微风拂面，心情舒畅的陈国强来到望了岭山顶，远远就听到鼓掌的声音，且周围围着好几位观众，走近一看，是一位太极师父正在耍太极拳，见他一招一式、行云流水、緾绕轻灵、舒展大方，如七尺罗衣挂在无影树上，随微风飘拽，这身手真是佩服。这太极拳与陈国强师师父原先学的杨式太极拳85式似曾相似，但又不一样。后来了解到这位师父耍的太极拳是九诀八十一式原始杨式太极拳，叫黄师父，名德苏(曾任阳东外事办局长，阳东区政府办公室主任，措词凌厉，文笔生辉，堪称阳东第一笔)。由于羡慕，驱使陈国强师父向黄师父请教，一交流，彼此投缘，就这样，每个周末早晨，他们都相约在望了岭山顶练功场，虚心好学的陈国强在黄师父悉心指导下，很快掌握了九诀八十一式杨式太极拳，并接触到太极推手，深感太极拳之奥妙，不知不觉三年过去了。</p><p><br></p><p>　　2001年初，黄师父将陈国强推荐给他的师父冯万志（曾任阳江市服退军人医院副院长，资深老中医），冯万志师父对九诀八十一式杨式太极拳杨有颇深研究，造旨很深，太极拳推手更是神妙莫测，与人一交手，对方可应声倒地而没受到伤害。从此，陈国强又在冯万志师父的指导下，继续深入研习九诀八十一式杨式太极拳，同时也学会太极剑和太极推手。</p><p><br></p><p>　　太极推手属技击风格，以柔制刚，其手法可以控制对手的身体与关节，以杨式太极拳的套路为基础，加强杨式太极拳的实践作用，进而演化为散打的形式，达到强身护体的效果。陈国强练习太极推手后，感觉身体协调性、敏捷性更强，又有爆发力，既能修身、养性、强身、护体，又能全身心释放压力，展示太极拳更多的功效与魅力。</p><p><br></p><br><p><strong>对武当拳法的追求和渴望拥有</strong></p><br><p>　　陈国强老师每到周末早上都穿梭在北山公园、望了岭公园，只要有人练太极的地方都去观摩和拜访，也结识不少练太极的朋友，但几年过去终未得日思夜想的武当拳法。真是踏破铁鞋无觅处，得来全不费工夫。2004年春节初三的旁晚，他独自一人到阳江中心广场准备耍耍太极，看到远处草地边一个约五十岁、身体魁梧，身穿褐色衣服像一位老道正在教一高挑偏瘦的年青小伙，那小伙一会向东跌倒，一会从西爬起，只见那武师出手敏捷，一接手那小伙不是喊痛就是跌倒在地，演练的套路与陈国强老师先前所学截然不同，处处都是画圆，每一招一式都在抱球画圆，浑然一体，时疾时缓，时刚时柔，忽隐忽现，神出鬼没，看得眼睛都没眨一眨，直呼好拳法，打心底暗暗喜欢，认定这就是梦寐以求的拳法。于是上前拱手施礼，互道姓名，交谈中，这位老师很健谈，豪爽，自称半个老道，青年时是个武痴，云游四海、踏迹大半个中国名山古刹，遍访名师，身怀绝技，他就是张仁。(张仁，阳江人，1970年应征参加中国人民解放军，在南京服役，后退役定居南京)。他介绍刚才所演练的是武当赵堡太极拳，武当赵堡太极拳是最古老、最原汁原味的太极拳，原创于武当山祖师张三丰，由张三丰的弟子云游道长刘古泉传王宗岳，由王宗岳传至河南温县赵堡镇蒋发，从此武当赵堡太极拳在温县赵堡镇扎根，世代相传，虽已传数百年，但仍然完整和系统的保留张三丰原始拳架、推手、散手，三者融为一体的特色，是一种性命双修的内功修炼。陈老师第一次听到见到这种武当拳法，欣喜若狂，便跪下磕头拜师，</p><p><br></p><p>　　机缘这么巧合让陈老师遇上了，他非常珍惜。原本张仁师父准备是过完年回南京，因与陈老师的邂逅，被陈老师的勤学苦练、吃苦耐劳和待师热情的真情所感动，足足推迟了三个月的时间，言传身教，陪陈老师苦练百日筑基功，陈老师也不负张师父的重望，在短短的百余日，初步掌握了武当赵堡太极拳拳法及一般技法应用。张师父在回南京前，多次叮嘱要珍惜这“金不换”武当拳法，师父只带进门，修行靠个人，你要潜心研究这拳的上中下三盘技法，要躬身实践太极拳法中的往返必带折叠。更要理解和体悟《太极拳论》中”着熟逐悟懂，劲懂劲阶级神明”的道理。</p><p><br></p><p>　　张师父的话音，常常在陈老师耳边回响，他不敢懈怠，每天天濛濛亮就跑上望了岭练功所场，练上20遍，全身都湿透才下山，又匆匆忙忙赶去上班，下午5点多不管刮风下雨又到市公安局对面的鸳鸯湖公园草坪练几趟，冬来暑往坚持又懈。每年公休假，都急急忙忙搭上北上列车，来到千里之外的南京找师父正架和指点，十年下来收获颇丰。</p><p><br></p><p>　　千里求学感动张仁师父，每次到来，张师父总放下所有的一切事情，手把手言传身教，将几十年练拳心得和技法全盘传授给陈国强老师，因来一趟南京不易，为了争取更多时间，张师父将陈国强安排在自己家住，同住同吃，每天大清早，吃个馍头、一个鸡蛋和一杯豆浆就来到南京长江大桥公园练功场，首先一起站桩20分钟，再练拳架、推手，最后教太极棍。同时也带陈老师认识一些南京有名的太极拳大师，如张照奇(武当赵堡太极拳第十二代传承人)和南京理工大学的艾学林老师。</p><p><br></p><p>　　张师父常常提到南京理工大学的艾学林老师，说艾老师人品很好，很慈祥，是一个书法家和国画大师，且功夫很好，擅长擒拿摔打，对武当赵堡太极拳上中下三盘24法掤捋挤按、采挒肘靠、起落进退、腾闪圆转、缠跪挑撩，劈壁挂蹬很精通，是徐效昌传承给他的，也是南京学习武当赵堡太极拳最早的首批弟子。徐效昌是侯春秀的入室子弟，将武当赵堡太极拳传入南京的第一人。这样陈老师打心底暗暗羡慕和起敬。</p><p><br>　　2012年10月深秋时节，南京景色别致，秋风起，黄叶飞，梧桐大道两侧的梧桐树散发出金色的光芒，落叶满地黄金甲，陈国强老师又来到南京，这次张仁老师专程陪陈国强老师到南京理工大学艾学林老师的修炼道场，艾老师正在指导他的学员(其中有大学教授、大学生及外国留学研究生等)在练功。见张仁到来，便停下来，热情打招呼并一一作介绍。热情好客、和霭可亲的艾学林老师与陈国强一见如故，很投缘，为方便向艾学林老师请教，陈国强当晚就住在南京理工大学招待所，一段师生情缘也就从此开始。<br></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/08/23/a80bae0c455a4fb6be3e780a1e429826.png\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"></p><p style=\"text-align: center;\"><span style=\"font-size: 17px\"><span style=\"font-family: -apple-system, BlinkMacSystemFont, Helvetica Neue, PingFang SC, Hiragino Sans GB, Microsoft YaHei UI, Microsoft YaHei, Arial, sans-serif\"><span style=\"color: rgb(255, 41, 65);\"><span style=\"background-color: rgb(25, 25, 25);\">陈国强与师父张仁合照</span></span></span></span></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/08/23/cf8c5b2ea9184b1fb52ba91530699260.png\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"><br><span style=\"font-size: 17px\"><span style=\"font-family: -apple-system, BlinkMacSystemFont, Helvetica Neue, PingFang SC, Hiragino Sans GB, Microsoft YaHei UI, Microsoft YaHei, Arial, sans-serif\"><span style=\"color: rgb(255, 41, 65);\"><span style=\"background-color: rgb(25, 25, 25);\">陈国强与师父艾学林合照</span></span></span></span></p><p><br></p><p>　　两年后，陈国强练太极拳达到更高水准，他深感武当赵堡太极拳高深莫测，玄之又玄，他仍想深研精学，艾学林推荐陈国强到西安，拜武当赵堡太极拳第十一代传人、当代中国候氏太极拳掌门人侯转运为师。</p><p><br></p><p>　　艾学林师从徐孝昌，徐孝昌的师傅侯春秀，是张三丰武当太极拳第十代传人。当时，太极拳领域，两郑一侯名满西安，分别为：郑悟清，郑百英，侯春秀。侯春秀宗师擅长采挒跌放技法，更精散打功夫，功夫造诣上已达出神入化、炉火纯青之上乘境界。侯转运为侯春秀宗师的二儿子，继承了侯春秀宗师的精髓拳技，陈国强跟随侯转运深造拳技。</p><p><br></p><p>　　2016年，陈国强得到恩师侯转运的厚爱，在众多师兄弟的见证下，正式被侯转运纳为入室弟子，与其它来自五洲四海的弟子一起，正式拜当代中国侯氏太极掌门人侯转运为师，当时举办了隆重的拜师仪式，拜天地、拜祖师爷、拜师傅...... 并由师傅侯转运亲自颁发武当赵堡承架三合一太极拳十二代传人和中国侯氏太极拳第三代传人证书。从此，陈国强师兄弟遍布全国各地，每年都汇聚西安或武当山，参加帮派举办的拳技研讨会、研习会，直至如今。</p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/02/%E9%A2%81%E5%8F%91%E8%AF%81%E4%B9%A61.jpg\" alt=\"\" title=\"\" width=\"400\" data-display=\"inline\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/02/%E7%AC%AC%E4%B8%89%E4%BB%A3%E4%BC%A0%E4%BA%BA%E8%AF%81%E4%B9%A6.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/08/23/638a8842bcb74701ad511aa98a493c7f.png\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/02/%E4%BE%AF%E6%98%A5%E7%A7%80.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"></p><p style=\"text-align: center;\"><br></p><p>　　2017年，陈国强的一位师兄，在师傅、师伯、所有师兄弟的支持与配合下，于武当山成立和太极学院，目标为促使全球太极拳联盟，意在全球推广武当赵堡太极拳。为此，侯转运师傅，要求全国各地每一位入室弟子，在其当地推广武当赵堡太极拳。</p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/02/d762f64f3d24424aa13324f1f2370351.png\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/02/2017%E5%B9%B4%E7%AC%AC%E5%85%AD%E5%B1%8A%E6%AD%A6%E5%BD%93%E5%9B%BD%E9%99%85%E6%BC%94%E6%AD%A6%E5%A4%A7%E8%B5%9B%E5%9B%A2%E9%98%9F.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"></p><p style=\"text-align: center;\">参加2017年第六届武当国际演武大赛团队</p><p style=\"text-align: center;\"><br></p><p>　　为了将武当赵堡太极拳在阳江地区宣传、推广、发展，首要任务就要建立一个武当赵堡太极拳培训基地。在阳江市老干部局副局长许方强、阳江市老干部大学校长容振标，以及校领导梁华森副校长、陈加强副校长和校办公室主任叶植众的大力支持下，于2017年3月12日在阳江老干部大学隆重举办武当赵堡太极养生班开班仪式，陈国强被聘任为阳江市老干部大学武当赵堡太极拳总教练。</p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/02/%E9%99%88%E5%9B%BD%E5%BC%BA%E5%85%88%E7%94%9F%E8%A2%AB%E8%81%98%E4%BB%BB%E4%B8%BA%E9%98%B3%E6%B1%9F%E5%B8%82%E8%80%81%E5%B9%B2%E9%83%A8%E5%A4%A7%E5%AD%A6%E6%AD%A6%E5%BD%93%E8%B5%B5%E5%A0%A1%E5%A4%AA%E6%9E%81%E6%8B%B3%E6%80%BB%E6%95%99%E7%BB%83.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/02/%E5%88%9B%E5%8A%9E%E9%98%B3%E6%B1%9F%E5%B8%82%E6%AD%A6%E5%BD%93%E8%B5%B5%E5%A0%A1%E5%A4%AA%E6%9E%81%E6%8B%B3%E5%9F%B9%E8%AE%AD%E5%9F%BA%E5%9C%B0.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"></p><p><br></p><p>　　武当赵堡太极拳养生班在阳江市老干部大学里的设立，标志着武当赵堡太极拳，从此在阳江扎根，在阳江开技散叶，蓬勃发展。武当赵堡太极拳依托阳江市老干部大学，积极参与全市各大型文艺汇演，将武当赵堡太极拳这一传统文化展现在广大观众页面。</p><p><br></p><p>　　老干部大学武当赵堡太极拳养生班初成立时，学生只有10来人。陈国强非常努力，先从自己所在工作单位市公安局着手，成立前卫体协太极拳俱乐部，对全市公安机关民警进行免费培训。之后，报名学习太极拳的人数日渐增长，陈国强任劳任怨，拼尽全力将武当赵堡太极拳传播开来。一日，阳江市开放大学（原阳江广播电视大学）培训科科长，主动征求陈国强的意见，在开放大学开设赵堡太极拳兴趣班，由陈国强担任总教练，免费教喜欢练太极拳的学生学习，达到推广武当赵堡太极拳的效果。</p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/02/6613eda463404a51adfd10d17c8ab31f.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/08/23/fa1ca6f8d7d14137b13a17b3691870bc.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"><br></p><p>　　太极拳作为中国的一种传统武术，练习太极拳对于弘扬民族传统美德、构建和谐社会具有重要的意义。</p><p><br></p><p>　　2017年，陈国强参加了第六届武当国际演武大赛并荣获一等奖，被第六届武当国际演武大会组委会评为当代武当赵堡太极拳名师，并颁发证书。</p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/08/23/ccae121511ee4b35a0306c5ceca0da8a.png\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/02/%E5%A5%96%E6%9D%AF.jpg\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/02/6.jpg\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"></p><p><br></p><p>　　2018年2月，在阳江市福兴园AAA景区举行阳江市老干部大学武当赵堡太极拳培训基地挂牌仪式，出席挂牌仪式的领导有阳江市老干部大学校长容振标，学校办公室主任叶植众、阳江市福兴园董事长林元福先生、阳江市乡情网总编莫良钢先生，以及武当赵堡太极拳团队成员。</p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/02/1609fc91e6eb4742a74c677c501efef8.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"><br></p><p>　　陈国强有了将武当赵堡太极拳推广进校园的经验，主动拜访阳江市职业技术学院体育系主任郑海，分享了其想在职业技术学院推广武当赵堡太极拳的想法，其想法当场得到郑海主任的大力支持。事后，武当赵堡太极拳走进入职业技术学院体育系，成为最受大学生们欢迎的体育选修课。</p><p><br></p><p>　　职业技术学院开办大规模校运动会，陈国强组织了五六十名跟他练习武当赵堡太极拳的老师和学生，在运动会开幕仪式中展演，让人眼前一亮。通过此次赵堡太极拳的展示，白沙技工学校的校长，亦有意愿在学校内推广武当赵堡太极拳。</p><p><br></p><p>　　2018年11月4日，阳江白沙技工学校开办体育运动会，陈国强已组织好该校三十名学生，经过20天的习训，练习武当赵堡太极拳13式，同时结合自己赵堡太极拳的学生，共计五六十人，在此次运动会开幕仪式中展演。有了之前职业技术学院校运会展演的经验，此次白沙技工学校校运会的赵堡太极拳展演，表现更自然，队伍动作更整齐，更受学生、老师、领导们称赞。学校领导非常赞同在学校内推广赵堡太极拳。</p><p><br></p><p>　　2019年11月28日，陈国强带领阳江市技工院校200多名师生和武当赵堡太极广东阳江分会会员，在广东省技工院校教研会第六届田径会开幕式上表演武当赵堡太极拳。广东省省级技工院校田径会，两年举办一届，此次田径会开幕式上，陈国强带领师生表演武当赵堡太极拳，成为其人生最有成就感的事。</p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/08/23/7e2ecc063f0e435f869cba8dd99831c0.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/06/02/2a3c51700b7844a1ad16b0d240eec241.png\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"><br><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/08/23/6d5f5f015d6b4eb89cead7dcbc1d7092.png\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"></p><p><br></p><p><strong>关于工作与教学时间的安排</strong></p><p>　　陈国强就职于公安局110指挥中心，有时候24小时值班，下班之后休息两天，陈国强利用自己休息时间，到各大院校或事业机关单位教授和推广太极拳。</p><p><br></p><p><strong>太极拳是老年人运动是一种误解</strong></p><p>　　人们普遍认为，太极拳是一种慢速健身运动，适合老年人练习。</p><p><br></p><p>　　陈国强纠正：太极拳有三种练法，早上练快速太极拳，下午3、4点钟练刚猛太极拳，晚上才练柔慢太极拳，人们只看到一些老大爷老大妈练慢速柔性太极拳，所以才会认为练太极拳是老年人运动。人到老年，筋骨变硬，练太极拳有一定难度。<span style=\"font-size: 13px\"><span style=\"font-family: Arial, sans-serif\"><span style=\"color: rgb(51, 51, 51);\"><span style=\"background-color: rgb(255, 255, 255);\">有一句武术谚语</span></span></span></span>，太极十年不出门，少林拳三年打天下。修练太极拳要十年，才能达到一定成效。所以练太极拳，要从年轻时期开始。人年轻时，筋骨柔软，随着年龄增长，体格变硬朗，练太极拳可达到刚柔并济的显著成效。</p><p><br></p><p><strong>是否持续公益推广武当赵堡太极拳</strong></p><p>　　陈国强表示，练太极拳为个人爱好，推广武当赵堡太极拳为完成师傅交予的重任。</p><p>陈国强也意识到，大面积公益推广武当赵堡太极拳，会影响以授武维生者的收入。陈国强就是否持续公益推广武当赵堡太极拳的问题，征求师傅的意见。陈国强师傅支持陈国强持续公益推广武当赵堡太极拳，未来桃李遍天下，好友常伴左右，自己心安理得，是难得的人生一大乐事！</p><p><br></p><p><strong>武当赵堡太极拳简介</strong></p><p>　　武当赵堡太极拳是最古老、最原汁原味的太极拳，源创于武当山祖师张三丰，是一种性命双修的保健运动，具轻灵圆活，舒展大方。它通过调心、调息、调身的修练，内固精神、外练筋骨、调行气血、疏通经络。同时，有以柔克刚，四两拨千斤，后发先制的武术特点。亦有动如行云流水，绵绵不断，刚柔相济的武术风格。更有发人潜能，开人智慧，壮人体魄，去病健身，益寿延年的独特功效，实为中华武术之精华。太极拳运动既自然又高雅，练习者可亲身体会到音乐的韵律、哲学的内涵、美的造型、诗的意境。在高级的享受中，使疾病消失，使身心健康。</p><p><br><strong>荣誉与风采</strong></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/08/23/0f7d8e6ee4be4574a9904dab031e4e17.png\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"></p><p style=\"text-align: center;\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/08/24/32ac99e991d34c1d99a4622a25b337a2.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/08/24/e4669d52bf1344f2bb4cecf8227942cf.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/08/24/f3f2685e0e5f43e1a34a919f77efbeb1.jpg\" alt=\"\" title=\"\" width=\"500\" data-display=\"inline\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/08/24/0412d7e7ddf7445b8c5fd36744a1675b.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/08/24/339cc5b1ffad46c9b961979d76e19a96.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/08/24/2a34bd7254974925bf86c251f9e8c528.jpg\" alt=\"\" title=\"\" width=\"600\" data-display=\"inline\"><img src=\"https://jili20.oss-cn-shenzhen.aliyuncs.com/article/2022/08/24/d39670986b034a11bad2278976aeff6b.jpg\" a