insert  into `background`(`id`,`user_id`,`status`,`url`,`is_deleted`,`create_time`,`update_time`) values (1522193827748720641,1,'1','https://s3.bmp.ovh/imgs/2022/05/05/5dd1c1cdd57069ab.png','\0','2022-05-05 20:37:44','2022-05-05 20:37:44'),(1522195213077000194,1,'1','https://i.postimg.cc/3JpxwpJ0/show.gif','\0','2022-05-05 20:43:14','2022-05-05 20:43:14'),(1522195579390734338,1,'1','https://s3.bmp.ovh/imgs/2022/05/05/d8f659bf0c26e3e9.jpeg','\0','2022-05-05 20:44:42','2022-05-05 20:44:41'),(1522195806835257345,1,'1','https://s3.bmp.ovh/imgs/2022/05/05/d43faeda4f0c6ec7.gif','\0','2022-05-05 20:45:36','2022-05-05 20:45:36'),(1522198507589545985,1,'1','https://s3.bmp.ovh/imgs/2022/05/05/0783bb0301055f4e.gif','\0','2022-05-05 20:56:20','2022-05-05 20:56:19'),(1522207905284173826,1,'1','https://s3.bmp.ovh/imgs/2022/05/05/fb61f9f70aa84e7e.gif','\0','2022-05-05 21:33:41','2022-05-05 21:33:40'),(1522210699978289153,1,'1','https://s3.bmp.ovh/imgs/2022/05/05/0bb1fe7de7a37598.gif','\0','2022-05-05 21:44:47','2022-05-05 21:44:46'),(1522212515092705282,1,'1','https://s3.bmp.ovh/imgs/2022/05/05/577564ed9465c1d9.gif','\0','2022-05-05 21:52:00','2022-05-05 21:51:59');

/*Table structure for table `category` */

DROP TABLE IF EXISTS `category`;

CREATE TABLE `category` (
  `id` smallint unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `parent_id` smallint unsigned NOT NULL DEFAULT '0' COMMENT '父分类ID',
  `category_name` varchar(10) NOT NULL DEFAULT '' COMMENT '分类名称',
  `level` smallint unsigned NOT NULL DEFAULT '1' COMMENT '层级：（1 一级；2 二级）',
  `sort` smallint unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `is_deleted` bit(1) NOT NULL DEFAULT b'0' COMMENT '状态：（ 0 正常；1 已删除）',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `idx_parent_id` (`parent_id`)
) ENGINE=InnoDB AUTO_INCREMENT=94 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='文章分类表';

