/*Table structure for table `comment` */

DROP TABLE IF EXISTS `comment`;

CREATE TABLE `comment` (
  `id` bigint unsigned NOT NULL COMMENT 'ID',
  `user_id` bigint unsigned NOT NULL DEFAULT '0' COMMENT '留言人ID',
  `article_id` bigint unsigned NOT NULL DEFAULT '0' COMMENT '文章ID',
  `reply_count` int unsigned NOT NULL DEFAULT '0' COMMENT '回复总数',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '留言内容',
  `username` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '留言人名',
  `avatar` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '留言者头像',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`),
  KEY `idx_article_id` (`article_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci COMMENT='评论表';

/*Data for the table `comment` */

insert  into `comment`(`id`,`user_id`,`article_id`,`reply_count`,`content`,`username`,`avatar`,`create_time`) values (1520609791225974785,1,1515597815407980546,2,'穷则独善其身，达则兼济天下。','Bing','https://s3.bmp.ovh/imgs/2022/05/03/ad3375687205534f.png','2022-05-01 11:43:20'),(1520638251105374210,1,1515597815407980546,0,'请允许别人比我们优秀。','Bing','https://s3.bmp.ovh/imgs/2022/05/03/ad3375687205534f.png','2022-05-01 13:36:25');

/*Table structure for table `looper` */

DROP TABLE IF EXISTS `looper`;

CREATE TABLE `looper` (
  `id` bigint unsigned NOT NULL COMMENT 'ID',
  `status` char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '0' COMMENT '状态：（0 待审核; 1 审核通过；2 审核未通过）',
  `title` varchar(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '图片标题',
  `position` char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '0' COMMENT '位置：(0 首页顶部；1 页面右则；2 帖子正文下)',
  `user_id` bigint unsigned NOT NULL DEFAULT '0' COMMENT '发布者ID',
  `username` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '用户名',
  `looper_url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '图片',
  `looper_link` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '跳转的网址',
  `sort` int unsigned NOT NULL DEFAULT '1' COMMENT '排序',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci COMMENT='轮播图表';

