/*Table structure for table `one` */

DROP TABLE IF EXISTS `one`;

CREATE TABLE `one` (
  `id` bigint unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `avatar` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '赞助人头像',
  `username` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '赞助人昵称',
  `message` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '随机诗语',
  `link` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '个人经历网址',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '赞助时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci COMMENT='赞助1元表（个人网站版）';

/*Data for the table `one` */

insert  into `one`(`id`,`avatar`,`username`,`message`,`link`,`create_time`) values (1,'https://jili20.oss-cn-shenzhen.aliyuncs.com/default/bing.jpg','梦','如果您还想留下点什么，那么，请留下您的经历！','','2022-04-29 20:06:02'),(2,'https://jili20.oss-cn-shenzhen.aliyuncs.com/default/bing.jpg','梦','对过去的错误最好的弥补，就是在未来做正确的事情。','','2022-04-29 20:11:10'),(3,'https://jili20.oss-cn-shenzhen.aliyuncs.com/default/bing.jpg','梦','人生中很多阴影的出现，都是因为我们自己遮住了阳光。','','2022-04-29 20:12:06'),(4,'https://jili20.oss-cn-shenzhen.aliyuncs.com/default/bing.jpg','梦','一生温暖纯良，不舍爱与自由。','','2022-04-29 20:12:20'),(5,'https://jili20.oss-cn-shenzhen.aliyuncs.com/default/bing.jpg','梦','别人的成功，不会让我们变得渺小，让我们变得渺小的是对别人的嫉妒之心。','','2022-04-29 20:12:33'),(6,'https://jili20.oss-cn-shenzhen.aliyuncs.com/default/bing.jpg','梦','只要来日可期，今天就值得欣喜。','','2022-04-29 20:12:46'),(7,'https://jili20.oss-cn-shenzhen.aliyuncs.com/default/bing.jpg','梦','我不知道将要去何方，但我已在路上。','','2022-04-29 20:13:00'),(8,'https://jili20.oss-cn-shenzhen.aliyuncs.com/default/bing.jpg','梦','把你的脸迎向阳光，那就不会有阴影。','','2022-04-29 20:13:12'),(9,'https://jili20.oss-cn-shenzhen.aliyuncs.com/default/bing.jpg','梦','纵有疾风起，人生不言弃。','','2022-04-29 20:13:28'),(10,'https://jili20.oss-cn-shenzhen.aliyuncs.com/default/bing.jpg','梦','跌入低谷，并不意味着要一直在那里。','','2022-04-29 20:13:40'),(11,'https://jili20.oss-cn-shenzhen.aliyuncs.com/default/bing.jpg','梦','所谓能耐，就是既有能力又懂忍耐。','','2022-04-29 20:13:52'),(12,'https://jili20.oss-cn-shenzhen.aliyuncs.com/default/bing.jpg','梦','不喊痛，不一定没感觉；不要求，不一定没期待；不落泪，不一定没伤痕；不说话，不一定没心声。','','2022-04-29 20:14:03'),(13,'https://jili20.oss-cn-shenzhen.aliyuncs.com/one/2022/1.jpg','快乐就好','世界上没有两片完全相同的叶子，也没有两个完全相同的人！有些事，经历过，有些人，相遇过，那些深深浅浅的人生痕迹，是残留在沧桑岁月里的精美诗句，成为我们生命中的经典!','','2022-07-14 01:50:58'),(14,'https://jili20.oss-cn-shenzhen.aliyuncs.com/one/2022/1.jpg','快乐就好','美好的一天从欣赏，善待，赞美，学习，理解，包容和感恩开始！欣赏别人是一种境界，善待别人是一种胸怀，关心别人是一种品质，理解别人是一种涵养，帮助别人是一种快乐，学习别人是一种智慧，包容别人是一种能力，借鉴别人是一种收获，赞美别人是一种支持，理解别人是一种宽容，感恩别人是一种升华！学最好的别人，做最好的自己，生活才辉煌！','','2022-07-15 00:00:31');

/*Table structure for table `picture` */

DROP TABLE IF EXISTS `picture`;

CREATE TABLE `picture` (
  `id` bigint unsigned NOT NULL COMMENT '主键',
  `type` char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '0' COMMENT '类型（0 所有图片；1 在用图片；3 已删除图片）',
  `url` mediumtext CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '图片地址',
  `article_id` bigint unsigned NOT NULL DEFAULT '0' COMMENT '文章ID',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`),
  KEY `idx_article_id` (`article_id`)
