/*Table structure for table `reply` */

DROP TABLE IF EXISTS `reply`;

CREATE TABLE `reply` (
  `id` bigint unsigned NOT NULL COMMENT 'ID',
  `comment_id` bigint unsigned NOT NULL DEFAULT '0' COMMENT '留言ID',
  `from_user_id` bigint unsigned NOT NULL DEFAULT '0' COMMENT '回复人ID',
  `to_user_id` bigint unsigned NOT NULL DEFAULT '0' COMMENT '回复的目标用户ID',
  `root_id` bigint unsigned NOT NULL DEFAULT '0' COMMENT '根回复ID，如果没有，是留言ID',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '回复内容',
  `from_username` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '回复用户名',
  `from_avatar` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '回复用户头像',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`),
  KEY `idx_comment_id` (`comment_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci COMMENT='回复表';

/*Data for the table `reply` */

insert  into `reply`(`id`,`comment_id`,`from_user_id`,`to_user_id`,`root_id`,`content`,`from_username`,`from_avatar`,`create_time`) values (1520636956680245250,1520609791225974785,1,1,1520609791225974785,'总有一天，我们都会成为世界的过客，所以，勇敢去热爱去生活，将沉闷的日子，活成值得铭记的时刻。','Bing','https://s3.bmp.ovh/imgs/2022/05/03/ad3375687205534f.png','2022-05-01 13:31:17'),(1520638421910016001,1520609791225974785,1,1,1520636956680245250,'不管什么天气，记得随时带上自己的阳光。不管什么遭遇，记得内心装满开心的童话。','Bing','https://s3.bmp.ovh/imgs/2022/05/03/ad3375687205534f.png','2022-05-01 13:37:07');

/*Table structure for table `sys_menu` */

DROP TABLE IF EXISTS `sys_menu`;

CREATE TABLE `sys_menu` (
  `id` int unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `parent_id` int unsigned NOT NULL DEFAULT '0' COMMENT '父菜单ID ( 0为顶级菜单 )',
  `name` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '菜单名称',
  `is_deleted` bit(1) NOT NULL DEFAULT b'0' COMMENT '逻辑删除:(0 false 正常；1 true 已删除 ）',
  `url` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '请求地址',
  `type` char(1) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '1' COMMENT '菜单类型：( 1 目录；2 菜单；3 按钮 )',
  `code` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '授权标识符',
  `icon` varchar(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '图标',
  `sort` smallint unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `remark` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '备注',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `idx_menu_name` (`name`)
) ENGINE=InnoDB AUTO_INCREMENT=96 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci COMMENT='权限菜单表';

