/*Table structure for table `v_category_total` */

DROP TABLE IF EXISTS `v_category_total`;

/*!50001 DROP VIEW IF EXISTS `v_category_total` */;
/*!50001 DROP TABLE IF EXISTS `v_category_total` */;

/*!50001 CREATE TABLE  `v_category_total`(
 `value` bigint ,
 `name` varchar(10) 
)*/;

/*Table structure for table `v_month_article_total` */

DROP TABLE IF EXISTS `v_month_article_total`;

/*!50001 DROP VIEW IF EXISTS `v_month_article_total` */;
/*!50001 DROP TABLE IF EXISTS `v_month_article_total` */;

/*!50001 CREATE TABLE  `v_month_article_total`(
 `year_month` varchar(7) ,
 `total` bigint 
)*/;

/*View structure for view v_category_total */

/*!50001 DROP TABLE IF EXISTS `v_category_total` */;
/*!50001 DROP VIEW IF EXISTS `v_category_total` */;

/*!50001 CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `v_category_total` AS select ifnull(count(`a`.`id`),0) AS `value`,`c`.`category_name` AS `name` from (`article` `a` left join `category` `c` on((`a`.`category_pid` = `c`.`id`))) where (`a`.`publish` = 1) group by `a`.`category_pid` */;

/*View structure for view v_month_article_total */

/*!50001 DROP TABLE IF EXISTS `v_month_article_total` */;
/*!50001 DROP VIEW IF EXISTS `v_month_article_total` */;

/*!50001 CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `v_month_article_total` AS select `t1`.`year_month` AS `year_month`,count(`t2`.`id`) AS `total` from ((select date_format(curdate(),'%Y-%m') AS `year_month` union select date_format((curdate() - interval 1 month),'%Y-%m') AS `year_month` union select date_format((curdate() - interval 2 month),'%Y-%m') AS `year_month` union select date_format((curdate() - interval 3 month),'%Y-%m') AS `year_month` union select date_format((curdate() - interval 4 month),'%Y-%m') AS `year_month` union select date_format((curdate() - interval 5 month),'%Y-%m') AS `year_month`) `t1` left join `article` `t2` on(((`t1`.`year_month` = date_format(`t2`.`create_time`,'%Y-%m')) and (`t2`.`publish` = 1)))) group by `t1`.`year_month` */;

